INSERT INTO `post` (`id`, `writer`, `title`, `content`, `status`) VALUES
(1, 'asdf', 'Lịch sử của môn thể thao leo núi', '<p><span style=\"font-size:18px\">Leo n&uacute;i, c&ograve;n được gọi l&agrave; leo n&uacute;i, m&ocirc;n thể thao m&agrave; người tham gia phải đạt được, hoặc cố gắng đạt được, c&aacute;c điểm cao ở c&aacute;c v&ugrave;ng n&uacute;i, chủ yếu l&agrave; để tạo niềm vui khi leo n&uacute;i. Mặc d&ugrave; thuật ngữ n&agrave;y thường được &aacute;p dụng lỏng lẻo cho việc đi bộ l&ecirc;n những ngọn n&uacute;i thấp chỉ g&acirc;y kh&oacute; khăn ở mức độ vừa phải, nhưng n&oacute; bị hạn chế th&iacute;ch hợp hơn đối với việc leo n&uacute;i ở những địa phương c&oacute; địa h&igrave;nh v&agrave; điều kiện thời tiết tiềm ẩn những nguy hiểm như vậy. Để đảm bảo an to&agrave;n, bạn phải c&oacute; một số kinh nghiệm nhất định trước đ&oacute;. Đối với những người chưa qua đ&agrave;o tạo, leo n&uacute;i l&agrave; một tr&ograve; ti&ecirc;u khiển nguy hiểm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp;<a href=\"https://images.immediate.co.uk/production/volatile/sites/7/2018/01/EP085-83f726c.jpg?quality=90&amp;resize=620%2C411&amp;fbclid=IwAR1o-0XzzCx2L5bgB2Y_tVagwfxA27rk3r1N5w8hLEaV_Awcbd9FdxMDhec\"><img alt=\"\" src=\"https://images.immediate.co.uk/production/volatile/sites/7/2018/01/EP085-83f726c.jpg?quality=90&amp;resize=620%2C411&amp;fbclid=IwAR1o-0XzzCx2L5bgB2Y_tVagwfxA27rk3r1N5w8hLEaV_Awcbd9FdxMDhec\" style=\"height:476px; width:479px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Leo n&uacute;i kh&aacute;c với c&aacute;c m&ocirc;n thể thao ngo&agrave;i trời ở chỗ chỉ ri&ecirc;ng địa h&igrave;nh của m&ocirc;i trường đ&atilde; đủ cung cấp h&agrave;nh động &mdash; v&agrave; tất cả c&aacute;c thử th&aacute;ch &mdash; cho người tham gia. Leo n&uacute;i l&agrave; hiện th&acirc;n của cảm gi&aacute;c mạnh được tạo ra bằng c&aacute;c thử th&aacute;ch l&ograve;ng can đảm, sự th&aacute;o v&aacute;t, tinh ranh, sức mạnh, khả năng v&agrave; sức chịu đựng của mỗi con người ở mức tối đa trong t&igrave;nh huống rủi ro vốn c&oacute;.Leo n&uacute;i ở một mức độ kh&oacute; hơn so với c&aacute;c m&ocirc;n thể thao kh&aacute;c, l&agrave; hoạt động theo nh&oacute;m, với mỗi th&agrave;nh vi&ecirc;n đều hỗ trợ lẫn nhau ở mọi giai đoạn. Đối với hầu hết c&aacute;c nh&agrave; leo n&uacute;i, th&uacute; vui leo n&uacute;i kh&ocirc;ng chỉ nằm ở việc &ldquo;chinh phục&rdquo; một đỉnh n&uacute;i m&agrave; c&ograve;n ở sự thỏa m&atilde;n về thể chất v&agrave; tinh thần nhờ nỗ lực c&aacute; nh&acirc;n m&atilde;nh liệt, tr&igrave;nh độ ng&agrave;y c&agrave;ng cao v&agrave; tiếp x&uacute;c với sự h&ugrave;ng vĩ của thi&ecirc;n nhi&ecirc;n.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Lịch sử</span></strong></p>\r\n\r\n<p><span style=\"font-size:18px\">Những nỗ lực ban đầu để leo l&ecirc;n c&aacute;c đỉnh n&uacute;i được lấy cảm hứng từ những động cơ kh&aacute;c ngo&agrave;i thể thao: để x&acirc;y dựng b&agrave;n thờ hoặc để xem liệu c&aacute;c linh hồn c&oacute; thực sự &aacute;m ảnh những độ cao từng bị cấm hay kh&ocirc;ng, để c&oacute; được c&aacute;i nh&igrave;n tổng quan về v&ugrave;ng qu&ecirc; l&acirc;n cận của một người hoặc để thực hiện c&aacute;c quan s&aacute;t kh&iacute; tượng hoặc địa chất. Trước kỷ nguy&ecirc;n hiện đại, lịch sử ghi nhận rất &iacute;t nỗ lực leo l&ecirc;n c&aacute;c đỉnh n&uacute;i chỉ v&igrave; mục đ&iacute;ch đạt được th&agrave;nh tựu. Trong suốt thế kỷ 18, ng&agrave;y c&agrave;ng c&oacute; nhiều nh&agrave; triết học tự nhi&ecirc;n - những nh&agrave; khoa học c&ugrave;ng thời với họ - bắt đầu thực hiện c&aacute;c chuyến đi thực tế đến d&atilde;y Alps của ch&acirc;u &Acirc;u để thực hiện c&aacute;c quan s&aacute;t khoa học. Khu vực xung quanh Chamonix, Ph&aacute;p, trở th&agrave;nh một điểm thu h&uacute;t đặc biệt đối với c&aacute;c nh&agrave; điều tra bởi v&igrave; c&aacute;c s&ocirc;ng băng lớn tr&ecirc;n chuỗi Mont Blanc.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://bloximages.chicago2.vip.townnews.com/syvnews.com/content/tncms/assets/v3/editorial/8/db/8dba8a21-51ef-59ea-95bf-832c2e31c5de/563c1bb15318b.image.jpg?crop=360%2C360%2C140%2C0&amp;resize=1200%2C1200&amp;order=crop%2Cresize&amp;fbclid=IwAR3g_MBTzMdbi6mENUDjS3Br5DfeeSA0xoEZ4rIUJrD6467RzUELH6xcliU\" style=\"height:500px; width:479px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Leo n&uacute;i theo nghĩa thể thao đương đại được sinh ra khi một nh&agrave; khoa học trẻ Genevese, Horace-B&eacute;n&eacute;dict de Saussure, trong chuyến thăm đầu ti&ecirc;n đến Chamonix v&agrave;o năm 1760, đ&atilde; xem Mont Blanc (ở độ cao 15,771 feet [4,807 m&eacute;t] l&agrave; đỉnh cao nhất ở ch&acirc;u &Acirc;u) v&agrave; x&aacute;c định rằng anh ta sẽ leo l&ecirc;n đỉnh của n&oacute; hoặc chịu tr&aacute;ch nhiệm về việc n&oacute; bị leo l&ecirc;n. &Ocirc;ng đ&atilde; đề nghị tiền thưởng cho lần đầu ti&ecirc;n đi l&ecirc;n Mont Blanc, nhưng phải đến năm 1786, tức hơn 25 năm sau, số tiền của &ocirc;ng mới được nhận - một b&aacute;c sĩ Chamonix, Michel-Gabriel Paccard, v&agrave; người khu&acirc;n v&aacute;c của &ocirc;ng, Jacques Balmat. Một năm sau, de Saussure tự m&igrave;nh leo l&ecirc;n đỉnh Mont Blanc. Sau năm 1850, c&aacute;c nh&oacute;m leo n&uacute;i người Anh với hướng dẫn vi&ecirc;n người Thụy Sĩ, &Yacute; hoặc Ph&aacute;p đ&atilde; lần lượt đến c&aacute;c đỉnh n&uacute;i cao của Thụy Sĩ. Một cuộc leo n&uacute;i mang t&iacute;nh bước ngoặt trong sự ph&aacute;t triển của m&ocirc;n thể thao n&agrave;y l&agrave; lần đầu ti&ecirc;n leo l&ecirc;n Matterhorn (14,692 feet [4,478 m&eacute;t]) ngoạn mục v&agrave;o ng&agrave;y 14 th&aacute;ng 7 năm 1865, bởi một nh&oacute;m do một nghệ sĩ người Anh, Edward Whymper dẫn đầu.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://upload.wikimedia.org/wikipedia/commons/c/c1/Edmund_Hillary_and_Tenzing_Norgay.jpg?fbclid=IwAR35MDdU62lwobOLr-97YMP9rHA6gjyVwzyRcEPCXuxeVCAk_scWwY4Oamc\" style=\"height:286px; width:479px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp;V&agrave;o giữa thế kỷ 19, người Thụy Sĩ đ&atilde; ph&aacute;t triển một đội ngũ hướng dẫn vi&ecirc;n m&agrave; sự l&atilde;nh đạo của họ đ&atilde; gi&uacute;p đưa leo n&uacute;i trở th&agrave;nh một m&ocirc;n thể thao đặc biệt khi họ dẫn đầu từ đỉnh cao n&agrave;y đến đỉnh cao kh&aacute;c tr&ecirc;n khắp Trung &Acirc;u. Đến năm 1870, tất cả c&aacute;c đỉnh Alpine ch&iacute;nh đ&atilde; được thu nhỏ, v&agrave; những người leo n&uacute;i bắt đầu t&igrave;m kiếm c&aacute;c tuyến đường mới v&agrave; kh&oacute; hơn tr&ecirc;n c&aacute;c đỉnh đ&atilde; đi l&ecirc;n. Khi một số đỉnh nhỏ c&ograve;n lại của d&atilde;y Alps đ&atilde; bị vượt qua, v&agrave;o cuối thế kỷ 19, c&aacute;c nh&agrave; leo n&uacute;i đ&atilde; chuyển sự ch&uacute; &yacute; của họ đến d&atilde;y Andes ở Nam Mỹ, d&atilde;y Rocky ở Bắc Mỹ, Kavkaz ở r&igrave;a ph&iacute;a t&acirc;y của ch&acirc;u &Aacute;, c&aacute;c đỉnh của ch&acirc;u Phi, v&agrave; cuối c&ugrave;ng l&agrave; sự rộng lớn của d&atilde;y Himalaya. N&uacute;i Aconcagua (22.831 feet [6.959 m&eacute;t]), đỉnh cao nhất của d&atilde;y Andes, lần đầu ti&ecirc;n được leo l&ecirc;n v&agrave;o năm 1897, v&agrave; Grand Teton (13.770 feet [4.197 m&eacute;t]) ở D&atilde;y n&uacute;i Rocky của Bắc Mỹ đ&atilde; được thăng thi&ecirc;n v&agrave;o năm 1898. C&ocirc;ng tước người &Yacute; d &#39; Abruzzi v&agrave;o năm 1897 lần đầu ti&ecirc;n leo l&ecirc;n N&uacute;i St. Elias (18.008 feet [5.489 m&eacute;t]), nằm giữa ranh giới quốc tế của tiểu bang Alaska v&agrave; l&atilde;nh thổ Yukon của Hoa Kỳ, Canada, v&agrave; v&agrave;o năm 1906 đ&atilde; leo th&agrave;nh c&ocirc;ng Đỉnh Margherita trong D&atilde;y Ruwenzori (16.795 feet [5.119 m&eacute;t]) ở Đ&ocirc;ng Phi. Năm 1913, một người Mỹ, Hudson Stuck, đ&atilde; l&ecirc;n đỉnh Denali (Mount McKinley) ở Alaska, ở độ cao 20.310 feet (6.190 m&eacute;t), l&agrave; đỉnh cao nhất ở Bắc Mỹ. Con đường đ&atilde; mở ra cho những cuộc chinh phục lớn hơn, nhưng phải đến giữa thế kỷ trước khi ph&aacute;o đ&agrave;i cuối c&ugrave;ng, Đỉnh Everest tr&ecirc;n d&atilde;y Himalaya, được thăng thi&ecirc;n.<br />\r\nKhi thế kỷ 20 tr&ocirc;i qua, t&iacute;nh c&aacute;ch quốc tế thực sự của m&ocirc;n leo n&uacute;i bắt đầu bộc lộ. C&agrave;ng ng&agrave;y, người &Aacute;o, Trung Quốc, Anh, Ph&aacute;p, Đức, Ấn Độ, &Yacute;, Nhật Bản v&agrave; Nga c&agrave;ng ch&uacute; &yacute; đến c&aacute;c cơ hội vốn c&oacute; tr&ecirc;n v&ugrave;ng đất n&uacute;i lớn nhất h&agrave;nh tinh, d&atilde;y Himalaya v&agrave; c&aacute;c d&atilde;y l&acirc;n cận. Sau Thế chiến thứ nhất, người Anh đ&atilde; coi Everest l&agrave; mục ti&ecirc;u cụ thể của họ. Trong khi đ&oacute;, những người leo n&uacute;i từ c&aacute;c quốc gia kh&aacute;c đ&atilde; thực hiện những chuyến leo n&uacute;i th&agrave;nh c&ocirc;ng ngoạn mục l&ecirc;n c&aacute;c đỉnh Himalaya vĩ đại kh&aacute;c. Một đội Li&ecirc;n X&ocirc; đ&atilde; leo l&ecirc;n Đỉnh Stalin (24.590 feet [7.495 m&eacute;t]) - sau đ&oacute; được đổi t&ecirc;n th&agrave;nh Đỉnh Chủ nghĩa Cộng sản v&agrave; sau đ&oacute; l&agrave; Đỉnh Imeni Ismail Samani &mdash; tại Pamirs v&agrave;o năm 1933, một b&ecirc;n Đức đ&atilde; th&agrave;nh c&ocirc;ng tr&ecirc;n Siniolchu (22.600 feet [6.888 m&eacute;t]) v&agrave;o năm 1936, v&agrave; người Anh đ&atilde; leo l&ecirc;n Nanda Devi (25.646 feet [7.817 m&eacute;t]) trong c&ugrave;ng năm. V&agrave;o năm 1940&ndash;47, Tạp ch&iacute; Alpine của Lu&acirc;n Đ&ocirc;n, một bi&ecirc;n ni&ecirc;n sử đ&aacute;ng tin cậy về độ cao, lần đầu ti&ecirc;n liệt k&ecirc; kh&ocirc;ng c&oacute; đỉnh n&agrave;o đi l&ecirc;n &mdash; tất nhi&ecirc;n, phản &aacute;nh những mệnh lệnh của Thế chiến II.<br />\r\nTrong những năm 1950, một loạt c&aacute;c ngọn n&uacute;i nghi&ecirc;ng th&agrave;nh c&ocirc;ng tr&ecirc;n d&atilde;y Himalaya: lần đầu ti&ecirc;n người Ph&aacute;p leo l&ecirc;n Annapurna I (26.545 feet [8.091 m&eacute;t]) v&agrave;o th&aacute;ng 6 năm 1950, Nanga Parbat (26.660 feet [8.126 m&eacute;t]) bởi người Đức v&agrave; Người &Aacute;o v&agrave;o năm 1953, Kanchenjunga (28.169 feet [8.586 m&eacute;t]) bởi người Anh v&agrave;o th&aacute;ng 5 năm 1955, v&agrave; Lhotse I (27.940 feet [8.516 m&eacute;t]) bởi người Thụy Sĩ v&agrave;o năm 1956. Ngo&agrave;i ra, K2 trong D&atilde;y Karakoram, ở độ cao 28.251 feet ( 8.611 m&eacute;t) ngọn n&uacute;i cao thứ hai thế giới, lần đầu ti&ecirc;n được hai nh&agrave; leo n&uacute;i người &Yacute; thu nhỏ v&agrave;o th&aacute;ng 7 năm 1954. Tuy nhi&ecirc;n, vượt xa tất cả những điều đ&oacute;, sự th&agrave;nh c&ocirc;ng của người Anh tr&ecirc;n đỉnh Everest (29.035 feet [8.850 m&eacute;t]; xem Ghi ch&uacute; của nh&agrave; nghi&ecirc;n cứu: Chiều cao của đỉnh Everest ) - khi một người nu&ocirc;i ong ở New Zealand, Edmund (sau n&agrave;y l&agrave; Sir Edmund) Hillary, v&agrave; hướng dẫn vi&ecirc;n người T&acirc;y Tạng Tenzing Norgay đứng tr&ecirc;n đỉnh thế giới v&agrave;o ng&agrave;y 29 th&aacute;ng 5 năm 1953 - l&agrave; một thời khắc đỉnh cao. Cuộc th&aacute;m hiểm đ&oacute;, do Đại t&aacute; John Hunt dẫn đầu, l&agrave; đội thứ t&aacute;m trong v&ograve;ng 30 năm cố gắng l&ecirc;n đỉnh Everest, v&agrave; cũng đ&atilde; c&oacute; ba cuộc th&aacute;m hiểm do th&aacute;m.<br />\r\nMột nh&oacute;m người &Aacute;o đ&atilde; l&ecirc;n đến đỉnh Cho Oyu (26.906 feet [8.201 m&eacute;t]), ngay ph&iacute;a t&acirc;y của Everest, v&agrave;o th&aacute;ng 10 năm 1954. V&agrave;o th&aacute;ng 5 năm 1955, một nh&oacute;m người Ph&aacute;p đ&atilde; th&agrave;nh c&ocirc;ng trong việc đưa tất cả c&aacute;c th&agrave;nh vi&ecirc;n của m&igrave;nh v&agrave; một hướng dẫn vi&ecirc;n Sherpa l&ecirc;n đỉnh Makalu 1 (27,766 feet [8,463 m&eacute;t]), một người h&agrave;ng x&oacute;m kh&aacute;c của Everest. Đo&agrave;n th&aacute;m hiểm người Anh v&agrave;o th&aacute;ng 5 năm 1955 đ&atilde; leo l&ecirc;n Kanchenjunga, thường được coi l&agrave; một trong những thử th&aacute;ch leo n&uacute;i kh&oacute; nhất thế giới, do Charles Evans, người từng l&agrave; ph&oacute; trưởng đo&agrave;n leo Everest th&agrave;nh c&ocirc;ng đầu ti&ecirc;n dẫn đầu.<br />\r\nBắt đầu từ những năm 1960, hoạt động leo n&uacute;i đ&atilde; trải qua một số biến đổi. Khi c&aacute;c đỉnh n&uacute;i đ&atilde; được leo l&ecirc;n, sự ch&uacute; trọng chuyển sang việc t&igrave;m kiếm c&aacute;c tuyến đường ng&agrave;y c&agrave;ng kh&oacute; khăn l&ecirc;n từ đỉnh n&uacute;i đối diện với đỉnh, như trong thời kỳ v&agrave;ng son của c&aacute;c dốc n&uacute;i Alpine. Một v&iacute; dụ đ&aacute;ng ch&uacute; &yacute; l&agrave; cuộc leo l&ecirc;n Mặt T&acirc;y của Everest năm 1963 bởi hai th&agrave;nh vi&ecirc;n của đội Mỹ đầu ti&ecirc;n leo n&uacute;i. Hơn nữa, c&aacute;c mặt đ&aacute; thẳng đứng hay c&ograve;n gọi l&agrave; bất khả thi đ&atilde; được thu nhỏ th&ocirc;ng qua việc sử dụng c&aacute;c c&ocirc;ng cụ hỗ trợ nh&acirc;n tạo mới được ph&aacute;t triển v&agrave; c&aacute;c kỹ thuật leo n&uacute;i ti&ecirc;n tiến. C&aacute;c mặt thẳng đứng nhẵn b&oacute;ng của đ&aacute; granit đ&atilde; được khắc phục trong c&aacute;c cuộc leo n&uacute;i k&eacute;o d&agrave;i nhiều ng&agrave;y hoặc thậm ch&iacute; v&agrave;i tuần tại một thời điểm &mdash; v&iacute; dụ, cuộc chinh phục 27 ng&agrave;y của c&aacute;c nh&agrave; leo n&uacute;i Mỹ v&agrave;o năm 1970 tr&ecirc;n mặt đ&ocirc;ng nam tuyệt đối d&agrave;i 3.600 foot (1.100 m&eacute;t) của khối đ&aacute; granit El Capitan trong C&ocirc;ng vi&ecirc;n Quốc gia Yosemite ở d&atilde;y Sierra Nevada ở Bắc Mỹ. Những ph&aacute;t triển đ&aacute;ng ch&uacute; &yacute; kh&aacute;c bao gồm sự gia tăng phong c&aacute;ch leo l&ecirc;n c&aacute;c đỉnh cao nhất của &ldquo;Alpine&rdquo;, nơi những người leo n&uacute;i mang theo một lượng thiết bị v&agrave; vật tư tối thiểu v&agrave; kh&ocirc;ng phụ thuộc v&agrave;o nh&acirc;n vi&ecirc;n khu&acirc;n v&aacute;c v&agrave; c&aacute;c hỗ trợ b&ecirc;n ngo&agrave;i kh&aacute;c, v&agrave; sự gia tăng số lượng người leo n&uacute;i ở độ cao độ cao m&agrave; kh&ocirc;ng sử dụng oxy bổ sung.<br />\r\n&nbsp;<br />\r\n<img alt=\"\" src=\"http://kyluc.vn/Userfiles/Upload/images/410e.jpg\" style=\"height:359px; width:479px\" /></span><br />\r\n&nbsp;</p>\r\n', 1),
(3, 'asdf', 'Kĩ thuật leo núi', '<p style=\"text-align:center\"><strong><span style=\"font-size:24px\">Sau đ&acirc;y l&agrave; một số kỹ thuật leo n&uacute;i m&agrave; mọi người cần biết</span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 1: Kỹ thuật leo n&uacute;i</strong></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Tầm quan trọng của kỹ thuật leo n&uacute;i l&agrave; rất cần thiết. Khi bạn tập trung v&agrave;o kỹ thuật, di chuyển của cơ thể sẽ tự động v&agrave;o vị tr&iacute; v&agrave; bạn thấy m&igrave;nh dễ d&agrave;ng b&aacute;m tr&ecirc;n c&aacute;c tuyến đường đ&atilde; từng qu&aacute; kh&oacute; khăn. Phần n&agrave;y bao gồm một số kh&aacute;i niệm ch&iacute;nh:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">&nbsp;C&aacute;ch sử dụng ch&acirc;n của bạn</span></li>\r\n	<li><span style=\"font-size:18px\">&nbsp;C&aacute;ch duy tr&igrave; sự c&acirc;n bằng</span></li>\r\n	<li><span style=\"font-size:18px\">&nbsp;C&aacute;ch leo n&uacute;i hiệu quả hơn</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:24px\"><strong>KỸ thật leo n&uacute;i: Sử dụng ch&acirc;n của bạn</strong><br />\r\n<a href=\"file:///C:/xxamp/htdocs/baitap/project/Th%C6%B0%20mu%CC%A3c%20m%C6%A1%CC%81i/project/admin/content/img/su-dung-chan.jpg\"><img alt=\"\" longdesc=\"file:///C:/xxamp/htdocs/baitap/project/Th%C6%B0%20mu%CC%A3c%20m%C6%A1%CC%81i/project/admin/content/img/su-dung-chan.jpg\" src=\"https://4.bp.blogspot.com/-0rsZfiiCDPk/V7lpaqWL5zI/AAAAAAAAAm8/hIW3WIqny4w3zSlaJD5niOeYMkBKVa50ACLcB/s1600/giay-leo-nui-chinh-hang%2B1.jpg\" style=\"height:283px; width:600px\" /></a></span><br />\r\n<span style=\"font-size:18px\">B&agrave;n ch&acirc;n l&agrave; nền tảng của việc leo n&uacute;i. Rất nhiều người mới bắt đầu cố gắng k&eacute;o m&igrave;nh l&ecirc;n tường v&agrave; nhanh ch&oacute;ng mệt mỏi. H&atilde;y suy nghĩ về việc leo l&ecirc;n thang gấp &ndash; bạn kh&ocirc;ng n&ecirc;n tự k&eacute;o m&igrave;nh l&ecirc;n, bạn bước l&ecirc;n v&agrave; sử dụng tay để giữ thăng bằng. Điều n&agrave;y cũng giống như khi leo n&uacute;i.<br />\r\nC&aacute;c kỹ thuật cơ bản để sử dụng b&agrave;n ch&acirc;n của bạn l&agrave; edging v&agrave; smearing:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Edging c&oacute; nghĩa l&agrave; bạn bước l&ecirc;n v&agrave; b&aacute;m trụ bằng cao su tr&ecirc;n m&eacute;p gi&agrave;y của bạn. Bạn c&oacute; thể sử dụng cạnh b&ecirc;n trong, nơi ng&oacute;n ch&acirc;n c&aacute;i của bạn mang lại sự ổn định cho c&aacute;c chốt giữ nhỏ hơn hoặc bạn c&oacute; thể sử dụng cạnh b&ecirc;n ngo&agrave;i. Sự lựa chọn của bạn phụ thuộc v&agrave;o hướng bạn cần di chuyển để l&ecirc;n hoặc xuống.</span></li>\r\n	<li><span style=\"font-size:18px\">Smearing xảy ra khi bạn kh&ocirc;ng c&oacute; chỗ đứng thực sự, v&igrave; vậy bạn dựa v&agrave;o cao su gi&agrave;y của bạn để ma s&aacute;t với đ&aacute;. Smearing rất hữu &iacute;ch trong việc leo dốc, khi bạn l&ecirc;n đ&aacute; tr&ecirc;n g&oacute;c thấp m&agrave; kh&ocirc;ng c&oacute; nhiều chỗ đứng x&aacute;c định.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\">Khi bạn đang smearing, h&atilde;y t&igrave;m kiếm những vết l&otilde;m nhỏ hoặc phần nh&ocirc; ra sẽ tạo th&ecirc;m một ch&uacute;t ma s&aacute;t. Bạn cũng c&oacute; thể l&agrave;m phẳng c&aacute;c g&oacute;c để dễ d&agrave;ng b&aacute;m chặt hơn một ch&uacute;t.<br />\r\nH&atilde;y ghi nhớ c&aacute;c chiến thuật ch&acirc;n sau khi leo n&uacute;i:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Cố gắng giữ b&agrave;n ch&acirc;n của bạn ngay b&ecirc;n dưới bạn. Lu&ocirc;n quan s&aacute;t chỗ đứng ở vị tr&iacute; tốt nhất, để bạn c&oacute; thể duy tr&igrave; sự c&acirc;n bằng tốt hơn.</span></li>\r\n	<li><span style=\"font-size:18px\">T&igrave;m vị tr&iacute; đặt ch&acirc;n thay v&igrave; cho tay vịn.</span></li>\r\n	<li><span style=\"font-size:18px\">Một khi bạn đặt được ch&acirc;n, giữ y&ecirc;n vị tr&iacute;. Bạn c&oacute; thể c&oacute; cơ hội tốt hơn để giữ vững khi bạn thực hiện bước tiếp theo.</span></li>\r\n	<li><span style=\"font-size:18px\">Giữ g&oacute;t ch&acirc;n thấp để bạn tiếp x&uacute;c nhiều với tường. Với một đ&ocirc;i gi&agrave;y cao g&oacute;t &iacute;t cao su nằm tr&ecirc;n đ&aacute;, l&agrave;m giảm ma s&aacute;t v&agrave; tăng tỷ lệ cược m&agrave; bạn sẽ đẩy ch&acirc;n ra khỏi tường khi bạn thực hiện bước tiếp theo.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Kỹ thuật leo n&uacute;i: Duy tr&igrave; sự c&acirc;n bằng</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/giu-thang-bang.jpg\" style=\"height:305px; width:696px\" /></span><br />\r\n<span style=\"font-size:18px\">Khi bạn đủ may mắn để leo thẳng l&ecirc;n đỉnh, việc leo tr&egrave;o kh&aacute; trực quan. Tuy nhi&ecirc;n, khi bạn đi tr&ecirc;n một tuyến đường m&agrave; bạn phải di chuyển v&agrave; k&eacute;o theo c&aacute;c hướng kh&aacute;c nhau, bạn phải sử dụng cơ thể của m&igrave;nh để duy tr&igrave; sự c&acirc;n bằng.<br />\r\nKhi bạn phải sử dụng một nắm giữ đưa ra b&ecirc;n cạnh, bạn c&oacute; thể k&eacute;o thẳng xuống. V&igrave; vậy, bạn cần t&igrave;m c&aacute;ch chống lại lực k&eacute;o của b&ecirc;n đ&oacute;, để bạn kh&ocirc;ng mất thăng bằng v&agrave; ng&atilde; ra khỏi tường.<br />\r\nChiến thuật c&acirc;n bằng:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Nhấn b&agrave;n ch&acirc;n của bạn theo hướng ngược lại với lực k&eacute;o để tạo &aacute;p lực.</span></li>\r\n	<li><span style=\"font-size:18px\">K&eacute;o theo hướng ngược lại bằng tay kia hoặc ch&acirc;n bị m&oacute;c.</span></li>\r\n	<li><span style=\"font-size:18px\">Nghi&ecirc;ng người v&agrave; sử dụng trọng lượng cơ thể của bạn như một đối trọng để c&acirc;n bằng</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Kỹ thuật leo n&uacute;i: C&aacute;ch leo hiệu quả nhất</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/cach-leo.jpg\" style=\"height:768px; width:1024px\" /></span><br />\r\n<span style=\"font-size:18px\">T&igrave;m hiểu c&aacute;ch sử dụng &iacute;t năng lượng hơn v&agrave; l&agrave;m thế n&agrave;o để cơ bắp của bạn được nghỉ ngơi khi bạn leo l&ecirc;n:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">C&aacute;nh tay thẳng sẽ gi&uacute;p bạn đỡ mỏi mệt hơn. Duỗi thẳng c&aacute;nh tay cho ph&eacute;p bộ xương của bạn chiếm phần lớn trọng lượng chứ kh&ocirc;ng phải cơ bắp. Ngay cả một uốn cong nhẹ ở khuỷu tay của bạn c&oacute; nghĩa l&agrave; cơ bắp của bạn đang l&agrave;m việc để giữ n&oacute; ở vị tr&iacute; đ&oacute;.</span></li>\r\n	<li><span style=\"font-size:18px\">Tập trung v&agrave;o h&ocirc;ng của bạn. Người mới bắt đầu thường giữ h&ocirc;ng vu&ocirc;ng với tường, c&oacute; thể cảm thấy rất ổn định, nhưng n&oacute; đẩy trọng lượng của bạn ra khỏi tường v&agrave; l&agrave;m căng cơ bắp của bạn.</span></li>\r\n	<li><span style=\"font-size:18px\">Cố gắng giữ một h&ocirc;ng đẩy l&ecirc;n tường. Điều đ&oacute; gi&uacute;p giữ trọng lượng của bạn tr&ecirc;n b&agrave;n ch&acirc;n của bạn v&agrave; cho ph&eacute;p bạn dựa lưng với c&aacute;nh tay thẳng.</span></li>\r\n	<li><span style=\"font-size:18px\">C&oacute; một h&ocirc;ng s&aacute;t tường sẽ đưa vai của bạn gần hơn. C&acirc;n nặng của bạn đ&egrave; l&ecirc;n ch&acirc;n, l&agrave;m giảm cơ hội bị t&eacute;. Một vai s&aacute;t cũng thay đổi g&oacute;c k&eacute;o tr&ecirc;n tay cầm, gi&uacute;p ch&uacute;ng dễ cầm hơn.</span></li>\r\n	<li><span style=\"font-size:18px\">Những người leo n&uacute;i giỏi tr&egrave;o với mắt nh&igrave;n xung quanh. Giữ mắt của bạn tr&ecirc;n tường để t&igrave;m kiếm giữ cho ph&eacute;p bạn nghỉ ngơi nhanh ch&oacute;ng. Đừng chỉ tập trung v&agrave;o c&aacute;c dấu phấn.</span></li>\r\n	<li><span style=\"font-size:18px\">Khi bạn t&igrave;m thấy một vị tr&iacute; nghỉ ngơi tốt, sử dụng n&oacute;. Cho ph&eacute;p mạch của bạn chậm lại v&agrave; rũ bỏ c&aacute;nh tay của bạn để ch&uacute;ng kh&ocirc;ng bị căng qu&aacute; mức.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 2: Tư thế leo &nbsp;n&uacute;i</strong></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; C&oacute; kiến thức về tư thế leo n&uacute;i gi&uacute;p bạn giải quyết c&aacute;c vấn đề v&agrave; giải quyết c&aacute;c tuyến đường kh&oacute; khăn hơn. Mỗi động t&aacute;c n&agrave;y sử dụng c&aacute;c nguy&ecirc;n tắc được đề cập trong phần kỹ thuật, ở tr&ecirc;n.</span><br />\r\n<span style=\"font-size:24px\"><strong>Tư thế leo n&uacute;i: Bước l&ugrave;i</strong><br />\r\n&nbsp; </span><span style=\"font-size:18px\">&nbsp; Một bước l&ugrave;i l&agrave; ngược lại với một bước b&igrave;nh thường. Thay v&igrave; giẫm l&ecirc;n một ng&oacute;n ch&acirc;n với ng&oacute;n ch&acirc;n c&aacute;i v&agrave; h&ocirc;ng của bạn vu&ocirc;ng g&oacute;c với tường, bạn xoay h&ocirc;ng sang một b&ecirc;n v&agrave; bước với cạnh b&ecirc;n ngo&agrave;i (b&ecirc;n ng&oacute;n ch&acirc;n &uacute;t) của gi&agrave;y.<br />\r\nBước l&ugrave;i gi&uacute;p đưa h&ocirc;ng s&aacute;t tường, gi&uacute;p bạn duỗi thẳng tay v&agrave; nghỉ ngơi dễ d&agrave;ng hơn. N&oacute; cũng c&oacute; thể cung cấp đủ phần mở rộng để đạt được một nắm giữ kh&oacute; nắm bắt. C&aacute;c bước l&ugrave;i đặc biệt hữu &iacute;ch để tiết kiệm năng lượng tr&ecirc;n c&aacute;c tuyến đường dốc v&agrave; nh&ocirc; ra.</span><br />\r\n<span style=\"font-size:24px\"><strong>Tư thế leo n&uacute;i: Hạ đầu gối</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/ha-dau-goi.jpg\" style=\"height:576px; width:768px\" /><br />\r\n&nbsp; &nbsp;</span><span style=\"font-size:18px\"> Hạ đầu gối l&agrave; một bước l&ugrave;i lớn hơn. N&oacute; hoạt động tốt nhất khi bạn c&oacute; một chỗ đứng gần ngang h&ocirc;ng. Bước l&ecirc;n n&oacute; bằng đầu ng&oacute;n ch&acirc;n của bạn; sau đ&oacute; cuộn đầu gối của bạn v&agrave;o trong cho đến khi b&ecirc;n ngo&agrave;i gi&agrave;y của bạn nằm tr&ecirc;n đế v&agrave; đầu gối của bạn thực sự chỉ xuống.<br />\r\n&nbsp; &nbsp; Cũng giống như một bước l&ugrave;i, một đầu gối thả xuống h&ocirc;ng của bạn gần tường. N&oacute; rất tuyệt khi nghỉ ngơi, nhưng n&oacute; đặc biệt tuyệt vời khi bạn cần th&ecirc;m tầm với tr&ecirc;n một bức tường dốc hoặc nh&ocirc; ra.</span><br />\r\n<span style=\"font-size:24px\"><strong>Tư thế leo n&uacute;i: Ch&egrave;n ch&acirc;n</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/chen-chan.jpg\" style=\"height:301px; width:520px\" /><br />\r\n<strong>Tư thế leo n&uacute;i: Dựa v&agrave;o</strong></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Dựa v&agrave;o l&agrave; khi bạn k&eacute;o v&agrave; dựa v&agrave;o một b&ecirc;n của đ&aacute; hoặc vết nứt v&agrave; đẩy ch&acirc;n của bạn v&agrave;o ph&iacute;a b&ecirc;n kia. Khi bạn c&oacute; chỗ đứng tốt, tư thế dựa v&agrave;o rất hiệu quả v&igrave; c&aacute;nh tay của bạn thẳng v&agrave; b&agrave;n ch&acirc;n của bạn l&agrave;m c&ocirc;ng việc kh&oacute; khăn. Khi thiếu chỗ đứng tốt đ&ograve;i hỏi bạn phải gắng sức, h&atilde;y giữ g&oacute;t ch&acirc;n thấp để tối đa h&oacute;a lượng cao su chống lại đ&aacute;.</span><br />\r\n<span style=\"font-size:24px\"><strong>Tư thế leo n&uacute;i: Mantle</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/mantle.jpg\" style=\"height:392px; width:665px\" /></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Mantle l&agrave; tư thế khi bạn c&uacute;i xuống v&agrave; đưa ch&acirc;n l&ecirc;n để đụng tay. Một v&iacute; dụ kinh điển về mantle l&agrave; tr&ecirc;n đỉnh của một cuộc leo n&uacute;i khi bạn cần phải k&eacute;o m&igrave;nh l&ecirc;n gờ.<br />\r\nĐể mantle, ấn xuống giữ để c&oacute; trọng lượng của bạn tr&ecirc;n n&oacute;, sau đ&oacute; di chuyển ch&acirc;n l&ecirc;n để thay thế b&agrave;n tay của bạn.<br />\r\n&nbsp; &nbsp; Tư thế n&agrave;y l&agrave; cần thiết khi bạn đứng, nhưng cũng rất hữu &iacute;ch khi leo l&ecirc;n giữa khi bạn c&oacute; một nắm tay lớn v&agrave; bạn cần phải c&oacute; được đ&ocirc;i ch&acirc;n cao.</span><br />\r\n<span style=\"font-size:24px\"><strong>Tư thế leo n&uacute;i: B&aacute;m dưới</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/bam-duoi%20(1).jpg\" style=\"height:533px; width:800px\" /></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Giống như t&ecirc;n của n&oacute;, b&aacute;m ph&iacute;a dưới l&agrave; khi bạn sử dụng phần dưới của cơ thể. L&uacute;c đầu, việc sử dụng phần dưới cơ thể c&oacute; thể cảm thấy phản trực gi&aacute;c, v&igrave; thay v&igrave; k&eacute;o xuống giữ, bạn lại k&eacute;o l&ecirc;n.<br />\r\n&nbsp; &nbsp; Một ch&igrave;a kh&oacute;a cho tư thế n&agrave;y l&agrave; t&igrave;m ra chỗ đứng tốt, cao để bạn c&oacute; thể duy tr&igrave; sự căng thẳng của cơ thể bằng c&aacute;ch đẩy bằng ch&acirc;n khi bạn giữ vững. Giữ b&agrave;n ch&acirc;n của bạn cao đặt giữ xung quanh mức thắt lưng v&agrave; cho ph&eacute;p bạn giữ cho c&aacute;nh tay của bạn thẳng hơn.<br />\r\n&nbsp; &nbsp; Khi được thực hiện đ&uacute;ng, một tư thế tốt gi&uacute;p bạn ở một vị tr&iacute; vững chắc để đạt được một số độ cao th&ecirc;m.</span><br />\r\n<strong><span style=\"font-size:24px\">Tư thế leo n&uacute;i: K&eacute;o sang ngang</span></strong><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; K&eacute;o sang ngang l&agrave; sử dụng bất cứ phần n&agrave;o cơ thể để k&eacute;o sang một b&ecirc;n. Ch&igrave;a kh&oacute;a cho lực k&eacute;o sang nagng được thực hiện tốt l&agrave; bằng c&aacute;ch thay đổi trọng lượng cơ thể của bạn hoặc bằng c&aacute;ch sử dụng một lực đối kh&aacute;ng.<br />\r\n&nbsp; &nbsp; Giống như b&aacute;m ph&iacute;a dưới, k&eacute;o sang b&ecirc;n c&oacute; thể cảm thấy kh&oacute; xử v&igrave; bạn phải điều chỉnh hướng k&eacute;o của bạn theo hướng giữ. Nhưng một khi cảm gi&aacute;c đ&oacute; bắt đầu, đột nhi&ecirc;n bạn c&oacute; thể sử dụng giữ tất cả xung quanh bạn, kh&ocirc;ng chỉ những người ở tr&ecirc;n.</span><br />\r\n<span style=\"font-size:24px\"><strong>Tư thế leo n&uacute;i: Ch&egrave;n tay</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/chen-tay.jpg\" style=\"height:315px; width:696px\" /></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Ch&egrave;n tay l&agrave; phi&ecirc;n bản tay của smearing. Bạn chống lại tảng đ&aacute; với một l&ograve;ng b&agrave;n tay mở. Tư thế n&agrave;y c&oacute; thể gi&uacute;p bạn duy tr&igrave; sự c&acirc;n bằng trong khi bạn định vị lại đ&ocirc;i ch&acirc;n của m&igrave;nh. N&oacute; c&oacute; &iacute;ch nếu kh&ocirc;ng c&oacute; tay cầm tốt c&oacute; sẵn. Tư thế n&agrave;y cũng hữu &iacute;ch khi xuất ph&aacute;t v&igrave; n&oacute; cho ph&eacute;p bạn &aacute;p dụng &aacute;p lực đối với mặt trống. V&agrave; tr&ecirc;n c&aacute;c phiến leo, việc &eacute;p l&ograve;ng b&agrave;n tay xuống đặc biệt hữu &iacute;ch.<br />\r\n&nbsp; &nbsp; An to&agrave;n l&agrave; tr&aacute;ch nhiệm của bạn. Kh&ocirc;ng c&oacute; b&agrave;i viết hoặc video c&oacute; thể thay thế hướng dẫn v&agrave; kinh nghiệm th&iacute;ch hợp. H&atilde;y chắc chắn rằng bạn thực h&agrave;nh c&aacute;c kỹ thuật ph&ugrave; hợp v&agrave; hướng dẫn an to&agrave;n trước khi bạn leo l&ecirc;n.</span></p>\r\n', 1),
(5, 'asdf', 'Những nguy hiểm khi đi leo núi', '<p style=\"text-align:center\"><span style=\"font-size:24px\"><strong>Những nguy hiểm r&igrave;nh rập khi đi leo n&uacute;i</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Chinh phục những ngọn n&uacute;i h&ugrave;ng vĩ l&agrave; một trong những xu hướng du lịch mới của giới trẻ. Thế nhưng, phần lớn c&aacute;c bạn tham gia c&ograve;n chưa c&oacute; kinh nghiệm leo n&uacute;i, hoặc nhiều khi c&ograve;n chuẩn bị chưa kĩ c&agrave;ng g&acirc;y ra nguy hiểm cho bản th&acirc;n v&agrave; cả những người xung quanh. Để đảm bảo an to&agrave;n v&agrave; th&agrave;nh c&ocirc;ng cho chuyến đi, ch&uacute;ng t&ocirc;i xin n&ecirc;u ra một v&agrave;i&nbsp;nguy hiểm phổ biến nhất m&agrave; những người leo n&uacute;i thường gặp phải để c&aacute;c bạn lưu &yacute;.</span><br />\r\n<br />\r\n<span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 1. Mất nước</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/M%E1%BA%A5t%20n%C6%B0%E1%BB%9Bc.jpg\" style=\"height:335px; width:500px\" /></span><br />\r\n<span style=\"font-size:18px\">Nước lọc, nước &eacute;p tr&aacute;i c&acirc;y, sữa, nước dừa l&agrave; lựa chọn tuyệt vời kh&aacute;c. Khi leo n&uacute;i cơ thể rất dễ bị mất nước. Mất nước nhẹ v&agrave; trung b&igrave;nh thường c&oacute; thể được khắc phục bằng c&aacute;ch uống một số loại đồ uống để thay thế muối v&agrave; chất lỏng bị mất đi. Nước lọc l&uacute;c n&agrave;o cũng tốt đối với cơ thể. Nước &eacute;p tr&aacute;i c&acirc;y, sữa, nước dừa l&agrave; lựa chọn tuyệt vời kh&aacute;c. Sau khi tập luyện ở cường độ cao hoặc tham gia c&aacute;c hoạt động nặng nhọc như leo n&uacute;i, thức uống thể thao l&agrave; một sự bổ sung tuyệt vời, kh&ocirc;ng chỉ để b&ugrave; đắp lượng nước mất đi sự mất nước, m&agrave; c&ograve;n để bổ sung điện giải v&agrave; natri cho cơ thể. Nhưng lu&ocirc;n tr&aacute;nh xa đồ uống c&oacute; cồn v&agrave; caffein (chẳng hạn như c&agrave; ph&ecirc;, tr&agrave;, v&agrave; nước s&ocirc;-đa) bởi ch&uacute;ng c&oacute; xu hướng k&eacute;o nước khỏi cơ thể v&agrave; đẩy nhanh qu&aacute; tr&igrave;nh mất nước.</span><br />\r\n<span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 2. Muỗi v&agrave; vắt cắn</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/Mu%E1%BB%97i%20v%C3%A0%20v%E1%BA%AFt.jpg\" style=\"height:300px; width:500px\" /><br />\r\n&nbsp; &nbsp; </span><span style=\"font-size:18px\">Muỗi rừng sống hoang d&atilde; rất th&iacute;ch đốt m&aacute;u người, hoạt động mạnh khi trời tắt nắng. Muỗi c&oacute; thể g&acirc;y n&ecirc;n bệnh sốt r&eacute;t nguy hiểm đến t&iacute;nh mạng. C&aacute;c loại thuốc ph&ograve;ng ngừa muỗi đốt rất dễ t&igrave;m, c&oacute; thể mua tại c&aacute;c hiệu thuốc hoặc c&aacute;c cửa h&agrave;ng b&aacute;n đồ chuy&ecirc;n dụng cho d&acirc;n leo n&uacute;i. Nếu kh&ocirc;ng c&oacute;, bạn c&oacute; thể tạm thời thoa chanh l&ecirc;n c&aacute;c v&ugrave;ng da hoặc mang theo ch&uacute;t sả. Đ&acirc;y đều l&agrave; những loại hương liệu c&oacute; t&aacute;c dụng xua muỗi tự nhi&ecirc;n. Vắt thường đi t&igrave;m mồi từ 5 đến 8h s&aacute;ng hoặc 17 đến 19h tối. Ch&uacute;ng thường chọn nơi c&oacute; nhiệt độ ấm như sau gối, đ&ugrave;i, bẹn, lưng, n&aacute;ch, cổ... để h&uacute;t m&aacute;u người. Vắt c&oacute; khả năng b&aacute;m tr&ecirc;n gi&agrave;y, quần &aacute;o để t&igrave;m những nơi kh&ocirc;ng b&ocirc;i thuốc v&agrave; nơi c&oacute; thể chui v&agrave;o cơ thể người. Bạn cần cản thận b&ocirc;i c&aacute;c loại thuốc chống vắt l&ecirc;n to&agrave;n bộ phần da hở, c&aacute;c khe buộc gi&acirc;y, cổ gi&agrave;y, tất, phần ống quần, vai &aacute;o, mũ. Đừng qu&ecirc;n cho ống quần v&agrave;o trong tất. Ch&uacute; &yacute; ph&aacute;t hiện những con vắt b&ograve; tr&ecirc;n quần, &aacute;o để b&uacute;ng đi ngay v&igrave; ch&uacute;ng sẽ chui v&agrave;o người qua c&aacute;c mảng hở quần &aacute;o của bạn.</span><br />\r\n<span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 3. Lạc khỏi đo&agrave;n leo n&uacute;i</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/L%E1%BA%A1c%20%C4%91o%C3%A0n.jpg\" style=\"height:300px; width:500px\" /><br />\r\n&nbsp; &nbsp; </span><span style=\"font-size:18px\">Trong qu&aacute; tr&igrave;nh chinh phục n&uacute;i &ldquo;rừng&rdquo;, bạn c&oacute; thể gặp rất nhiều đường m&ograve;n do người d&acirc;n địa phương mở để đi, rất c&oacute; thể bạn sẽ đi nhầm đường nếu kh&ocirc;ng c&oacute; người chỉ dẫn, đặc biệt l&agrave; trong qu&aacute; tr&igrave;nh xuống với t&acirc;m l&yacute; chủ quan l&agrave; đ&atilde; nhớ đường. Quan trọng nhất l&agrave; lu&ocirc;n để &yacute; đến người dẫn đo&agrave;n. C&ograve;n nếu khi đi được một đoạn m&agrave; thấy đường lạ, kh&oacute; đi th&igrave; bạn cần phải quay lại ngay. Nếu lạc v&agrave; mất khả năng x&aacute;c định phương hướng, tốt nhất l&agrave; bạn kh&ocirc;ng n&ecirc;n đi tiếp m&agrave; ở y&ecirc;n chỗ đ&oacute; chờ người tới t&igrave;m, vừa tr&aacute;nh việc ti&ecirc;u hao năng lượng, tai nạn xảy ra, vừa để đội cứu hộ dễ khoanh v&ugrave;ng mất t&iacute;ch của bạn hơn. Phải nhớ hết sức b&igrave;nh tĩnh v&agrave; tập trung th&igrave; mới c&oacute; khả năng t&igrave;m về đo&agrave;n được.</span><br />\r\n<span style=\"font-size:24px\"><strong>4. Thận trọng với những đoạn đường nguy hiểm</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/Nguy%20hi%E1%BB%83m.jpg\" style=\"height:300px; width:500px\" /><br />\r\n&nbsp; &nbsp;</span><span style=\"font-size:18px\"> Đường leo n&uacute;i nhiều c&acirc;y cỏ, r&ecirc;u phong dễ trơn trượt, chỉ cần sơ sẩy một ch&uacute;t l&agrave; c&oacute; thể bị trượt ch&acirc;n ng&atilde; v&ocirc; c&ugrave;ng nguy hiểm. V&igrave; thế, việc trang bị ch&iacute;nh x&aacute;c dụng cụ như giầy, găng tay leo n&uacute;i, học trước những kỹ năng leo n&uacute;i trước khi l&ecirc;n đường l&agrave; điều rất cần thiết. Trong trường hợp gặp dốc đứng, bạn men theo triền để tiến l&ecirc;n theo h&igrave;nh chữ Z, sử dụng th&ecirc;m cả hai tay để hỗ trợ bằng c&aacute;ch b&aacute;m v&agrave;o đ&aacute;, th&acirc;n, rễ c&acirc;y... Nhưng nhớ ướm thử độ chắc chắn của những vật m&agrave; bạn d&ugrave;ng l&agrave;m điểm tựa.</span><br />\r\n<span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 5. Kiệt sức trong khi leo n&uacute;i</strong><br />\r\n<img alt=\"\" src=\"/i-upload/editor/images/Ki%E1%BB%87t%20s%E1%BB%A9c.jpg\" style=\"height:599px; width:900px\" /></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Người đi leo n&uacute;i nếu kh&ocirc;ng chuẩn bị cẩn thận rất dễ bị kiệt sức, đặc biệt v&agrave;o&nbsp;m&ugrave;a h&egrave; lại c&agrave;ng nguy hiểm.</span></li>\r\n	<li><span style=\"font-size:18px\">Những người bị bệnh tim mạch, huyết &aacute;p, suy h&ocirc; hấp kh&ocirc;ng n&ecirc;n leo n&uacute;i.Những ng&agrave;y trước khi leo n&uacute;i, bạn cần ăn đủ chất bổ dưỡng, bổ sung đầy đủ&nbsp;vitamin C, uống đủ nước, hạn chế rượu bia, thuốc l&aacute; v&agrave; quan trọng l&agrave; cần ngủ đủ 7&nbsp;giờ mỗi ng&agrave;y.</span></li>\r\n	<li><span style=\"font-size:18px\">Bạn c&oacute; thể tập đi bộ, chạy bộ, leo cầu thang, bơi, n&iacute;n thở trong v&ograve;ng 90 gi&acirc;y,&nbsp;đứng l&ecirc;n ngồi xuống, c&aacute;c b&agrave;i tập HIIT,... trước khi leo n&uacute;i từ một tuần đến mộtth&aacute;ng để tăng độ bền v&agrave; sức dẻo dai cho cơ thể.</span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:24px\">&nbsp; &nbsp; &nbsp;6. Lũ qu&eacute;t</span></strong><br />\r\n<span style=\"font-size:18px\">&nbsp;<strong>&nbsp; &nbsp; &nbsp; &nbsp;- Lũ qu&eacute;t l&agrave; g&igrave;?</strong><br />\r\n<br />\r\n&nbsp; &nbsp; Lũ qu&eacute;t l&agrave; hiện tượng một khối lượng nước khổng lồ di chuyển nhanh từ địah&igrave;nh cao xuống thấp, đi theo d&ograve;ng nước n&agrave;y c&oacute; thể l&agrave; đất đ&aacute;, c&acirc;y cối, khiến d&ograve;ng chảy c&oacute; thể hủy diệt mọi thứ tr&ecirc;n đường ch&uacute;ng đi qua.&nbsp;Vậy lượng nước đ&oacute; từ đ&acirc;u ra: Đ&oacute; l&agrave; nước mưa, nước chảy từ đỉnh n&uacute;i xuống,hay nước từ thượng nguồn của những con s&ocirc;ng, con suối, hoặc đ&ocirc;i khi c&oacute; thể l&agrave; nước xả lũ từ đập.&nbsp;Lũ qu&eacute;t thường xảy ra ở địa h&igrave;nh như thế n&agrave;o? Hiện tượng nguy hiểm n&agrave;y thường xảy ra tại c&aacute;c địa h&igrave;nh dốc như ch&acirc;n đồi n&uacute;i hoặc địa h&igrave;nh thung lũng. Ngo&agrave;i ra, lũ qu&eacute;t cũng xuất hiện ở v&ugrave;ng địa h&igrave;nh c&oacute; mật độ che phủ thực vật thấp, dẫn đến bề mặt lớp đất kh&ocirc;ng ổn định. T&ugrave;y thuộc v&agrave;o độ d&agrave;i qu&atilde;ng đường v&agrave; độ dốc địa h&igrave;nh m&agrave; n&oacute; qu&eacute;t qua, mức độ t&agrave;n ph&aacute; của lũ qu&eacute;t sẽ c&agrave;ng lớn khi mưa c&agrave;ng lớn, lượng nước c&agrave;ng khổng lồ v&agrave; di chuyển với tốc độ khủng khiếp.&nbsp;</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Đối với leo n&uacute;i, trekking th&igrave; vấn đề lũ qu&eacute;t c&oacute; thể ở những con suối tr&ocirc;ng hiền h&ograve;a nhưng bỗng chốc trở n&ecirc;n giận dữ v&agrave; hủy diệt mọi thứ - chủ yếu l&agrave; do mưa lớn ở thượng nguồn l&agrave;m tăng lưu lượng nước, hoặc đ&ocirc;i khi xảy ra ở những sườn n&uacute;i dốc.&nbsp;</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Để tr&aacute;nh gặp lũ qu&eacute;t v&agrave; những trường hợp kh&ocirc;ng mong muốn xảy ra, c&aacute;c bạn n&ecirc;n chọn cung đường trekking kỹ sao cho ph&ugrave; hợp với thời tiết tại thời điểm chuyến đi. Nếu trời mưa b&atilde;o qu&aacute; c&oacute; thể ho&atilde;n chuyến đi lại, v&agrave;o m&ugrave;a mưa th&igrave; hạn chế đi những cung đường đi ngang suối lớn như Ngũ Chỉ Sơn, Putaleng, Pusilung, T&agrave; năng cung th&aacute;c Yaly, &hellip;. hay bất kỳ cung đường n&agrave;o c&oacute; suối bắt ngang qua. Nếu kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c phải băng qua suối th&igrave; phải chuẩn bị đầy đủ những c&ocirc;ng cụ hỗ trợ, c&oacute; những giải ph&aacute;p đảm bảo an to&agrave;n trước khi băng qua suối. Đồng thời theo d&otilde;i sự thay đổi của nước khi đi qua suối hay đang đi dọc theo bờ suối. Nếu đột nhi&ecirc;n thấy nước trở n&ecirc;n đục hơn, mực nước hơi c&oacute; xu hướng d&acirc;ng l&ecirc;n, tr&ocirc;i theo d&ograve;ng nước bạn thấy c&oacute; thấy c&oacute; l&aacute; c&acirc;y, c&agrave;nh c&acirc;y kh&ocirc;, vỏ c&acirc;y mục, lắng nghe một x&iacute;u bạn c&oacute; thể nhận thấy &acirc;m thanh ầm ầm của đất đ&aacute;, c&agrave;nh c&acirc;y v&agrave; nước cuồn cuộn đang vọng tới- nhưng đừng c&oacute; đứng đ&oacute; m&agrave; lắng tai nghe l&acirc;u qu&aacute; kh&ocirc;ng hay đ&acirc;u&nbsp;h&atilde;y ngay lập tức tr&aacute;nh xa con suối đ&oacute;, d&ugrave; chỗ bạn kh&ocirc;ng hề mưa. Bởi đ&oacute; l&agrave; những dấu hiệu của một cơn lũ đang tới, từ thượng nguồn n&oacute; c&oacute; thể ập đến bất kỳ l&uacute;c n&agrave;o.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Tất nhi&ecirc;n bạn phải c&oacute; đủ kinh nghiệm, hoặc nếu chưa tự tin v&agrave; kinh nghiệm của bản th&acirc;n c&oacute; thể lựa chọn mua tour của một đơn vị n&agrave;o đ&oacute; - nơi m&agrave; những porter đ&atilde; qu&aacute; quen với cung đường đ&oacute; v&agrave; đủ khả năng xử l&yacute; khi c&oacute; t&igrave;nh huống bất ngờ xảy ra.&nbsp;</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 7. Sạt lở</strong></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; <strong>&nbsp; Sạt lở l&agrave; g&igrave;?</strong></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Sạt lở l&agrave; hiện tượng xảy ra khi mưa lớn k&eacute;o d&agrave;i, nước ngấm s&acirc;u v&agrave;o đất khiến cho đất mềm ra, độ li&ecirc;n kết của c&aacute;c khối đất với nhau bị giảm mạnh. C&aacute;c khối đất ở ven sườn đồi, s&ocirc;ng suối,.. khi đ&oacute; kh&ocirc;ng c&ograve;n đủ độ b&aacute;m với c&aacute;c khối ch&iacute;nh, kh&ocirc;ng thắng nổi trọng lực sẽ bị lở ra khỏi khối n&uacute;i/đồi ban đầu, g&acirc;y ra sạt lở. Th&ocirc;ng thường hiện tượng kh&ocirc; hạn k&eacute;o d&agrave;i trước m&ugrave;a mưa dễ g&acirc;y ra sự kh&ocirc; nứt trong l&ograve;ng đất, khiến cho việc sạt lở trở n&ecirc;n dễ d&agrave;ng hơn, c&oacute; nghĩa l&agrave; nếu thời tiết rất nắng n&oacute;ng trước khi trời mưa th&igrave; hiện tượng sạt lở sẽ c&agrave;ng dễ xảy ra. Đặc biệt đối với những sườn đồi kh&ocirc;ng thảm thực vật vững chắc, việc sạt lở lại c&agrave;ng l&agrave; một nguy cơ tiềm ẩn đặc biệt nguy hiểm.&nbsp; </span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; Khu vực n&agrave;o thường xảy ra sạt lở?</strong><br />\r\n<img alt=\"\" src=\"https://imagehub.fanfan.vn/fanfan/images/fanfan-nguy-hiem-khi-di-trekking-mua-mua-moi-nguy-hiem-khi-leo-nui-mua-03.png?d=s%2Ffiles%2FTips%2FNh%E1%BB%AFng%20nguy%20c%C6%A1%20ti%E1%BB%81m%20%E1%BA%A9n%20khi%20trekking%20-%20leo%20n%C3%BAi%20v%C3%A0o%20m%C3%B9a%20m%C6%B0a&amp;fbclid=IwAR2A9ogESuPNze1u9nGvm39YFkm4-5L4wuL6-OMe4qJA_5nBzFSh3wfDU_E\" /><br />\r\n&nbsp; &nbsp; Như đ&atilde; n&oacute;i ở nguy&ecirc;n nh&acirc;n h&igrave;nh th&agrave;nh sạt lở th&igrave; n&oacute; sẽ xảy ra ở những sườn n&uacute;i dốc, những v&aacute;ch n&uacute;i đất kh&ocirc;ng c&oacute; thảm thực vật chắc chắn. Tuy vậy mẹ thi&ecirc;n nhi&ecirc;n vẫn c&ograve;n đối xử tốt với d&acirc;n trekking rằng sạt lở đất kh&oacute; xảy ra hơn ở tr&ecirc;n rừng, hoặc tr&ecirc;n c&aacute;c khối n&uacute;i lớn, nơi những li&ecirc;n kết của đất vững v&agrave;ng hơn nhờ hệ thống rễ c&acirc;y v&agrave; c&aacute;c tảng đ&aacute; địa chất lớn cắm s&acirc;u trong l&ograve;ng đất - những g&igrave; nguy&ecirc;n thủy từ thi&ecirc;n nhi&ecirc;n, kh&ocirc;ng c&oacute; t&aacute;c động từ con người. V&iacute; dụ như c&aacute;c khối n&uacute;i lớn như ở Bạch Mộc Lương Tử, Lảo Thẩn hay Fansipan,... rất hiếm khi bị sạt lở đất. C&aacute;c v&ugrave;ng sườn gần đường đi, hay sườn đồi b&ecirc;n cạnh suối l&agrave; những vị tr&iacute; dễ xảy ra sạt lở nhất - c&oacute; thể l&agrave; do địa chất kh&ocirc;ng tốt, hoặc l&agrave; do t&aacute;c động của con người. Tuy nhi&ecirc;n nếu thời tiết dự b&aacute;o mưa lớn, b&atilde;o th&igrave; n&ecirc;n tr&aacute;nh những con suối chứ lũ qu&eacute;t m&agrave; kết hợp sạt lở th&igrave; combo n&agrave;y qu&aacute; lợi hại rồi.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; Trong leo n&uacute;i, trekking th&igrave; việc sạt lở thường &iacute;t gặp hơn so với lũ qu&eacute;t (Kh&ocirc;ng c&oacute; nghĩa l&agrave; kh&ocirc;ng gặp nh&eacute;!). Sự nguy hiểm của 2 hiện tượng n&agrave;y l&agrave; khi ta kh&ocirc;ng nhận ra kịp thời để ph&ograve;ng tr&aacute;nh n&oacute;, hay kh&ocirc;ng trong trạng th&aacute;i đề ph&ograve;ng khi đi qua cung đường c&oacute; nguy cơ cao. Nếu đủ kinh nghiệm v&agrave; kỹ năng th&igrave; trở ngại l&uacute;c n&agrave;y l&agrave; bạn phải chờ nước r&uacute;t hay bạn phải đi đường v&ograve;ng để ho&agrave;n th&agrave;nh chuyến đi. Một lần nữa nếu kh&ocirc;ng đủ kinh nghiệm th&igrave; bạn h&atilde;y chọn một anh porter để gửi gắm cho an to&agrave;n hơn nh&eacute;!</span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:24px\"><strong>NHỮNG LƯU &Yacute; ĐỂ TR&Aacute;NH RỦI RO KHI LEO N&Uacute;I</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><em>Chuẩn bị v&agrave; sử dụng thuần thục c&aacute;c thiết bị hỗ trợ leo n&uacute;i, tu&acirc;n thủ luật<br />\r\ncủa trưởng đo&agrave;n, chọn lối đi kỹ lưỡng v&agrave; thường xuy&ecirc;n theo d&otilde;i thời tiết l&agrave;<br />\r\nnhững điều bạn n&ecirc;n nhớ để tr&aacute;nh trường hợp xấu xảy ra khi leo n&uacute;i.</em></strong></span></p>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 1. Chuẩn bị vật dụng thiết yếu</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><img alt=\"\" src=\"https://thegioituingu.com/wp-content/uploads/2020/01/chuan-bi-dung-cu-leo-nui.jpg?fbclid=IwAR0iGzNymhqA51LUzMRyDXaqP_CP5ztY9520vzksI3lg5q8ogVc1yvpELdk\" /><br />\r\n&nbsp; &nbsp; Trước mỗi h&agrave;nh tr&igrave;nh leo n&uacute;i bạn cần biết r&otilde; những vật dụng m&igrave;nh cần chuẩn bị. Ngo&agrave;i c&aacute;c loại thuốc men, đồ sơ cứu, thực phẩm, quần &aacute;o&hellip; bạn c&ograve;n phải soạn đủ cho m&igrave;nh những thiết bị cần thiết như la b&agrave;n, d&acirc;y leo, m&aacute;y định vị, gậy, đ&egrave;n pin, pin dự ph&ograve;ng, c&ograve;i, dao nhỏ&hellip; Trong trường hợp gặp nguy hiểm chắc chắn bạn sẽ cần d&ugrave;ng đến. C&oacute; thời gian chuẩn bị kỹ lưỡng th&igrave; bạn n&ecirc;n sắp xếp ch&uacute;ng ở những vị tr&iacute; dễ lấy, đồng thời tập sử dụng cho thuần thục để kh&ocirc;ng bị l&uacute;ng t&uacute;ng khi d&ugrave;ng thật sự.<br />\r\n&nbsp; </span><span style=\"font-size:24px\"><strong>&nbsp; 2. Tu&acirc;n thủ c&aacute;c quy định của trưởng đo&agrave;n</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Người được chọn l&agrave;m trưởng đo&agrave;n thường phải l&agrave; người am hiểu lịch tr&igrave;nh v&agrave; nhiều kinh nghiệm đi nhất trong đo&agrave;n. Ch&iacute;nh v&igrave; vậy bạn n&ecirc;n ch&uacute; &yacute; lắng nghe v&agrave; l&agrave;m theo hướng dẫn của họ (mọi hiệu lệnh dừng nghỉ, đi nhanh hay chậm&hellip;) để h&agrave;nh tr&igrave;nh đảm bảo sự an to&agrave;n cho kh&ocirc;ng chỉ một m&agrave; tất cả mọi người. Ngo&agrave;i an to&agrave;n bạn c&ograve;n tận hưởng được trọn vẹn nơi m&igrave;nh đang đi qua. Đặc biệt kh&ocirc;ng n&ecirc;n t&aacute;ch đo&agrave;n đi ri&ecirc;ng, qu&aacute; chậm hoặc qu&aacute; nhanh so với mọi người trong đo&agrave;n dẫn đến trưởng đo&agrave;n kh&oacute; kiểm so&aacute;t v&agrave; chuyến đi sẽ mất vui. </span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 3. Chọn đường đi kỹ lưỡng v&agrave; ứng biến khi leo n&uacute;i</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Ngo&agrave;i việc nghi&ecirc;n cứu v&agrave; l&ecirc;n lịch tr&igrave;nh cẩn thận ra th&igrave; ứng biến khi chọn đường đi cũng rất cần thiết. N&ecirc;n lưu &yacute; những điều nhỏ nhưng cực kỳ quan trọng như men theo suối, đ&aacute;nh dấu đường&hellip; Khi đi theo lối ven suối bạn sẽ dễ d&agrave;ng c&oacute; nước để d&ugrave;ng nếu hết, ngo&agrave;i ra trường hợp bị lạc đường th&igrave; những người cứu hộ cũng sẽ t&igrave;m ở khu vực c&oacute; nước như s&ocirc;ng, suối &hellip; đầu ti&ecirc;n. Vừa đi bạn vừa phải đ&aacute;nh dấu lại đường để lưu &yacute; cho những người đi sau cũng như khi phải quay lại nếu gặp bất trắc kh&ocirc;ng thể ho&agrave;n th&agrave;nh h&agrave;nh tr&igrave;nh như: chấn thương, c&acirc;y đổ, n&uacute;i lở&hellip; </span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>&nbsp; &nbsp; 4. Thường xuy&ecirc;n theo d&otilde;i thời tiết</strong></span><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; Thời tiết l&agrave; yếu tố ảnh hưởng trực tiếp v&agrave; nhiều nhất tới chuyến leo n&uacute;i. Trước khi đi bạn chắc chắn phải xem dự b&aacute;o thời tiết hoặc thăm d&ograve; t&igrave;nh h&igrave;nh. Tuy vậy, đang tr&ecirc;n đường leo bạn vẫn phải thường xuy&ecirc;n quan s&aacute;t xung quanh để nhận biết sức gi&oacute;, hướng gi&oacute;, m&acirc;y m&ugrave;, sương, c&ocirc;n tr&ugrave;ng&hellip; Điều đ&oacute; gi&uacute;p bạn c&ugrave;ng đo&agrave;n c&oacute; thể đo&aacute;n biết sự thay đổi của thời tiết (nếu c&oacute;) để định hướng lối đi, dừng nghỉ hay tiếp tục.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1);
INSERT INTO `post` (`id`, `writer`, `title`, `content`, `status`) VALUES
(6, 'asdf', 'Các kiểu loại leo núi', '<p style=\"text-align:center\"><strong><span style=\"font-size:24px\">C&aacute;c loại h&igrave;nh leo n&uacute;i</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:24px\">1. Leo v&aacute;ch n&uacute;i tự nhi&ecirc;n &ndash; Bouldering</span></strong></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; loại h&igrave;nh leo v&aacute;ch n&uacute;i đ&aacute; bằng giầy leo n&uacute;i, sử dụng phấn. Th&ocirc;ng thường, c&oacute; thể sự dụng d&acirc;y an to&agrave;n neo từ tr&ecirc;n cao, người leo c&ograve;n c&oacute; thể tr&aacute;nh chấn thương bằng c&aacute;ch sử dụng miếng đệm đ&agrave;n hồi để giảm chấn thương v&agrave; đảm bảo an to&agrave;n cho người chơi. </span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">2. Leo c&aacute;c t&ograve;a th&aacute;p cao tầng &ndash; Buildering</span></strong></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; hoạt động leo thể thao mạo hiểm tr&ecirc;n c&aacute;c d&agrave;n khung th&eacute;p hoặc v&aacute;ch b&ecirc;n ngo&agrave;i của c&aacute;c t&ograve;a nh&agrave;, thường kh&ocirc;ng c&oacute; thiết bị bảo vệ v&agrave; kh&ocirc;ng khuyến kh&iacute;ch thực hiện. </span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">3. Leo v&aacute;ch n&uacute;i &ndash; Canyoneering</span></strong></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; hoạt động leo thể thao mạo hiểm v&agrave; giải tr&iacute; dọc theo c&aacute;c hẻm n&uacute;i cao thường được triển khai trong c&aacute;c tour kh&aacute;m ph&aacute; v&agrave; du lịch mạo hiểm, đặc l&agrave; trong c&aacute;c hang động hoặc tr&ecirc;n sa mạc.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>4. Leo n&uacute;i thi đấu thể thao &ndash; Competition climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Một m&ocirc;n thể thao thi đấu ch&iacute;nh thức được tổ chức gần đ&acirc;y, thường được tiến h&agrave;nh tr&ecirc;n c&aacute;c v&aacute;ch nh&acirc;n tạo do Li&ecirc;n đo&agrave;n Thể thao Quốc tế Leo n&uacute;i (IFSC) l&agrave; tổ chức ch&iacute;nh thức quản l&yacute; Leo n&uacute;i thi đấu tr&ecirc;n to&agrave;n thế giới v&agrave; được IOC v&agrave; GAISF c&ocirc;ng nhận v&agrave; l&agrave; th&agrave;nh vi&ecirc;n của Hiệp hội Thể thao Thế giới (IWGA). UIAA l&agrave; tổ chức ch&iacute;nh thức quản l&yacute; Competition climbing tr&ecirc;n băng tr&ecirc;n to&agrave;n thế giới. Giải thi đấu ch&iacute;nh thức c&oacute; ba h&igrave;nh thức: Lead, Bouldering and Speed.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>5. Leo n&uacute;i tự do &ndash; Free Climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Một h&igrave;nh thức leo n&uacute;i trong đ&oacute; người leo n&uacute;i sử dụng thiết bị leo n&uacute;i như d&acirc;y thừng v&agrave; c&aacute;c phương tiện bảo vệ an to&agrave;n kh&aacute;c bảo vệ chống lại chấn thương khi ng&atilde;.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>6. Leo n&uacute;i tuyết &ndash; Ice climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Leo tr&ecirc;n địa h&igrave;nh n&uacute;i Băng hoặc n&uacute;i tuyết đ&atilde; cứng bằng c&aacute;ch sử dụng c&aacute;c thiết bị đặc biệt, thường l&agrave; r&igrave;u đ&aacute;, m&oacute;c, kh&oacute;a. Thiết bị bảo vệ người leo n&uacute;i băng tương tự như leo n&uacute;i, với c&aacute;c thiết bị bảo vệ (như đinh v&iacute;t v&agrave; n&ecirc;m tuyết) th&iacute;ch nghi với điều kiện của m&ocirc;i trường đ&ocirc;ng lạnh.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>7. Leo n&uacute;i trong nh&agrave; &ndash; Indoor climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Kỹ thuật leo những bức tường nh&acirc;n tạo bouldering với c&aacute;c tay nắm (hold) đ&atilde; được gắn bu l&ocirc;ng bắt l&ecirc;n v&aacute;ch nh&acirc;n tạo của ph&ograve;ng tập leo n&uacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>8. Leo n&uacute;i Mountaineering</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; tập hợp c&aacute;c hoạt động leo d&atilde; ngoại li&ecirc;n quan tr&ecirc;n n&uacute;i bao gồm leo n&uacute;i ngo&agrave;i trời, đi bộ đường d&agrave;i, trượt tuyết v&agrave; ferrata. Mountaineering cũng thường được gọi l&agrave; alpinism, v&agrave; người leo n&uacute;i c&ograve;n gọi l&agrave; alpinists. UIAA &ndash; Li&ecirc;n đo&agrave;n leo n&uacute;i v&agrave; leo n&uacute;i quốc tế, l&agrave; cơ quan quản l&yacute; thế giới được Ủy ban Olympic quốc tế c&ocirc;ng nhận về leo n&uacute;i v&agrave; leo n&uacute;i, v&agrave; c&aacute;c vấn đề như Cứu hộ, Y Tế, Bảo vệ m&ocirc;i trường, An to&agrave;n tr&ecirc;n cao&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>9. Leo n&uacute;i đ&aacute; &ndash; Rock climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Leo l&ecirc;n tr&ecirc;n c&aacute;c v&aacute;ch đ&aacute; hoặc ngọn n&uacute;i đ&aacute; nh&ocirc; ra, sử dụng gi&agrave;y leo n&uacute;i v&agrave; t&uacute;i phấn v&agrave; c&aacute;c thiết bị như d&acirc;y thừng, bu l&ocirc;ng, đai ốc, hexes. Đặc biệt thường được t&aacute;i hiện c&aacute;c pha mạo hiểm nhờ v&agrave;o c&aacute;c thiết bị quay video chuy&ecirc;n dụng.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>10. Đu d&acirc;y &ndash; Rope climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; m&ocirc;n leo nhờ v&agrave;o một đoạn d&acirc;y ngắn v&agrave; to để thực hiện c&aacute;c động t&aacute;c leo tốc độ, kh&ocirc;ng giống với h&igrave;nh thức leo n&uacute;i hay leo n&uacute;i tuyết.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>11. Sport climbing &ndash; Leo n&uacute;i thể thao</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; h&igrave;nh thức leo n&uacute;i dựa tr&ecirc;n m&oacute;c neo vĩnh cửu cố định v&agrave;o đ&aacute; v&agrave; c&oacute; thể l&agrave; bu l&ocirc;ng để bảo vệ (ngược lại với leo n&uacute;i truyền thống, nơi đ&aacute; thường kh&ocirc;ng c&oacute; bắt neo v&agrave; bu l&ocirc;ng cố định).</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>12. Leo n&uacute;i c&oacute; neo dậy &ndash; Top roping</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; h&igrave;nh thức leo tr&ecirc;n một đường l&ecirc;n trong một v&aacute;ch hay một hẻm n&uacute;i, ph&iacute;a tr&ecirc;n được bảo vệ bởi một sợi d&acirc;y neo ở đầu v&agrave; được bảo vệ bởi một người giữ d&acirc;y neo b&ecirc;n dưới </span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">13. Leo n&uacute;i truyền thống &ndash; Traditional climbing (Trad climbing)</span></strong></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; một h&igrave;nh thức leo n&uacute;i m&agrave; kh&ocirc;ng c&oacute; neo v&agrave; bu l&ocirc;ng cố định. Người leo đặt bảo vệ c&oacute; thể th&aacute;o rời như c&aacute;c thiết bị cam, đai ốc v&agrave; c&aacute;c thiết bị kh&aacute;c giữ d&acirc;y nối v&agrave;o tảng đ&aacute; (th&ocirc;ng qua việc sử dụng carabiner v&agrave; d&acirc;y lanyar hoặc sling) để ph&ograve;ng tr&aacute;nh bị rơi.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>14. Leo n&uacute;i solo &ndash; Solo climbing</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">- L&agrave; một phong c&aacute;ch leo n&uacute;i, trong đ&oacute; người leo n&uacute;i tr&egrave;o l&ecirc;n một m&igrave;nh, m&agrave; kh&ocirc;ng ai đ&oacute; đang đi c&ugrave;ng họ để hỗ trợ. Khi leo n&uacute;i solo phải rất cẩn trọng để ph&ograve;ng bất cứ lỗi n&agrave;o c&oacute; thể xảy ra. V&igrave; chỉ một lỗi th&ocirc;ng thường m&agrave; kg c&oacute; người hỗ trợ rất dễ dẫn đến tai nạn, v&igrave; kh&ocirc;ng c&oacute; hệ thống belay. Vi vậy, Soloing cần phải nhiều kinh nghiệm, thệ lực tốt, độ tự tin cao để giảm thiểu tất cả c&aacute;c rủi ro.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngo&agrave;i ra cũng c&ograve;n nhiều loại h&igrave;nh leo thể thao giải tr&iacute; kh&aacute;c như: Chalk climbing, Scrambling, Tree climbing, Pole Climbing, Lumberjack tree. Tuy nhi&ecirc;n khi tham gia c&aacute;c hoạt động leo n&uacute;i người chơi cần phải lưu &yacute; vấn đề an to&agrave;n c&aacute; nh&acirc;n v&igrave; leo n&uacute;i l&agrave; loại h&igrave;nh thể thao mạo hiểm cần rất nhiều thể lực v&agrave; &yacute; ch&iacute; v&igrave; vậy phải thực hiện tốt c&aacute;c bước chuẩn bị như: Thể trạng c&aacute; nh&acirc;n, r&egrave;n luyện kỹ năng, Kinh nghiệm xử l&yacute;, Nước/ Thực phẩm, Vật dụng leo/ trang thiết bị c&aacute; nh&acirc;n, Khảo s&aacute;t địa h&igrave;nh, Thời gian leo, m&ocirc;i trường xung quanh khu vực leo, nh&oacute;m hỗ trợ kỹ thuật leo &hellip; Ch&uacute;c c&aacute;c bạn c&oacute; h&agrave;nh tr&igrave;nh chinh phục th&agrave;nh c&ocirc;ng những cung đường n&uacute;i h&ugrave;ng vĩ.</span></p>\r\n', 1),
(9, 'asdf', 'Cắm trại qua đêm khi leo núi', '<p style=\"text-align:center\"><span style=\"font-size:24px\"><strong>Nơi ch&uacute; ẩn khi leo n&uacute;i</strong></span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>1: Phải cần c&oacute; trại (lều)</strong></span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>2: Chọn địa điểm cắm trại cho ph&ugrave; hợp</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Lựa chọn điểm hạ trại l&agrave; một trong những điểm mấu chốt để c&oacute; một giấc ngủ ngon. Sẽ kh&ocirc;ng thể n&agrave;o ngủ được nếu chỗ bạn dựng lều đầy đ&aacute; hay rễ c&acirc;y b&ecirc;n dưới, cũng kh&ocirc;ng thể l&agrave; một nền đất ẩm ướt. Một vị tr&iacute; hạ trại ph&ugrave; hợp l&agrave; một mặt phẳng rộng, bằng phẳng, kh&ocirc;ng nghi&ecirc;ng v&agrave; cũng kh&ocirc;ng c&oacute; đ&aacute; hay rễ c&acirc;y lồi l&otilde;m dưới đ&aacute;y lều</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Nếu nơi cắm trại to&agrave;n đồi n&uacute;i v&agrave; địa h&igrave;nh gốc th&igrave; h&atilde;y chọn nơi &iacute;t nghi&ecirc;ng nhất</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Nếu cắm trại gần hồ, s&ocirc;ng, v&agrave; đặc biệt l&agrave; suối h&atilde;y x&aacute;c định một khoảng c&aacute;ch đủ an to&agrave;n để kh&ocirc;ng cần phải bưng lều chạy khi nước d&acirc;n l&ecirc;n, hay tệ hơn l&agrave; c&oacute; lũ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Đồng thời c&agrave;ng l&ecirc;n cao, nồng độ oxy trong kh&ocirc;ng kh&iacute; c&agrave;ng lo&atilde;ng, điều n&agrave;y &iacute;t nhiều sẽ ảnh hưởng đến h&ocirc; hấp v&agrave; gi&aacute;n tiếp ảnh hưởng đến chất lượng giấc ngủ của bạn. Sự lo&atilde;ng đi của nồng độ oxy trong kh&ocirc;ng kh&iacute; sẽ dễ nhận ra ở độ cao hơn 2000m, v&agrave; r&otilde; rệt hơn ở độ cao hơn 3000m, v&igrave; vậy kh&ocirc;ng n&ecirc;n ngủ lại, hạ trại ở những đỉnh n&uacute;i cao. Thay v&igrave; vậy h&atilde;y chọn địa điểm hạ trại ở những ngọn đồi, hay lưng n&uacute;i thấp hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://media.2dep.vn/upload/thuhuyennt/2021/05/18/kinh-nghiem-leo-nui-va-cam-trai-tai-nui-co-tien-ngam-toan-canh-nha-trang-tu-tren-cao-1621331992-1.jpg?fbclid=IwAR1NGI8kOkKnNQJD7mUYG4smNbZyFq5wcJ0vuw0n51532wkxRsePNFpdFeE\"><img alt=\"\" src=\"https://media.2dep.vn/upload/thuhuyennt/2021/05/18/kinh-nghiem-leo-nui-va-cam-trai-tai-nui-co-tien-ngam-toan-canh-nha-trang-tu-tren-cao-1621331992-1.jpg?fbclid=IwAR1NGI8kOkKnNQJD7mUYG4smNbZyFq5wcJ0vuw0n51532wkxRsePNFpdFeE\" style=\"height:624px; width:900px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>&middot; Mẹo để lựa chọn địa điểm cắm trại</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Gần nguồn nước.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Địa h&igrave;nh bằng phẳng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Tr&aacute;nh xa những mối nguy hiểm tiềm ẩn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Chỗ c&oacute; b&oacute;ng r&acirc;m.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Độ rộng bải cắm.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>3: Chuẩn bị đủ những trang bị cần thiết</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Nệm l&oacute;t hoặc chiết c&aacute;ch nhiệt: Bạn c&oacute; thể chọn nệm hợi hoặc chiếu c&aacute;ch nhiệt th&ocirc;ng thường.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - T&uacute;i ngủ ph&ugrave; hợp: Một chiếc t&uacute;i ngủ để đảm bảo bạn đủ ấm v&agrave; cho bạn cảm gi&aacute;c an to&agrave;n khi ngủ. T&uacute;i ngủ qu&aacute; mỏng sẽ kh&ocirc;ng đem lại cảm gi&aacute;c thoải m&aacute;i, kh&ocirc;ng đủ giữ ấm, t&uacute;i ngủ qu&aacute; d&agrave;y sẽ g&acirc;y hầm b&iacute;, kh&oacute; ngủ. N&ecirc;n việc lựa chọn t&uacute;i ngủ ph&ugrave; hợp cần dựa v&agrave;o nhiều yếu tố, nhưng quan trọng nhất l&agrave; nhiệt độ của điểm đến.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Gối hoặc bất kỳ từ g&igrave; c&oacute; thể gối: C&oacute; những bạn kh&ocirc;ng thể ngủ nết thiết gối, h&atilde;y trang bị một chiếc gối hơi, hoặc tận dụng bất kỳ thwuc g&igrave; c&oacute; thể gối v&iacute; dụ như &aacute;o kho&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Bịt mắt v&agrave; bịt tại nết bạn l&agrave; người nhạy cảm với &acirc;m thanh khi ngủ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://vcdn1-dulich.vnecdn.net/2020/09/10/1-1599734042.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LYoAzy53mfxNc0Mhko13EQ&amp;fbclid=IwAR1oxDnKFii06LI9VbhLZ7YVaZ-iPmDX8LhZkWSoXfCM8M5qr-DZIrQRV7M\" style=\"height:900px; width:720px\" /></span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>4: Một số mẹo gi&uacute;p dễ ngủ hơn</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Ngăn kh&ocirc;ng cho muỗi v&agrave; c&ocirc;n tr&ugrave;ng v&agrave;o lều: Thật kh&oacute; chịu nếu đang lim dim ngủ m&agrave; ở đ&acirc;u cứ vo ve vo ve tiếng c&ocirc;n tr&ugrave;ng bay, chưa kể l&acirc;u l&acirc;u c&ograve;n bị muỗi ch&iacute;ch kh&ocirc;ng thể n&agrave;o ngủ được. V&igrave; vậy h&atilde;y đảm bảo kh&ocirc;ng c&oacute; một con muỗi hay b&eacute; c&ocirc;n tr&ugrave;ng n&agrave;o c&oacute; thể lọt v&agrave;o trong lều bằng c&aacute;ch sau khi dựng lều, h&atilde;y đảm bảo lớp cửa lưới được kh&oacute;a lại, c&oacute; thể mở lớp cửa vải để lều được th&ocirc;ng tho&aacute;ng nhưng h&atilde;y chắc chắn l&agrave; bạn đ&atilde; kh&oacute;a lớp cửa lưới lại nh&eacute;. M&agrave; muốn l&agrave;m được như vậy th&igrave; lều của bạn phải c&oacute; lớp lưới ngăn c&ocirc;n tr&ugrave;ng, h&atilde;y ch&uacute; &yacute; điều n&agrave;y khi mua hay thu&ecirc; một chiếc lều cắm trại.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Kh&ocirc;ng uống nước qu&aacute; nhiều trước khi ngủ: Hẳn l&agrave; kh&ocirc;ng ai muốn đang ấm &aacute;p trong t&uacute;i ngủ lại dấn th&acirc;n v&agrave;o b&oacute;ng tối lạnh lẽo để đi vệ sinh, v&agrave; tệ hơn l&agrave; đi một m&igrave;nh nhỉ? Việc bổ sung nước l&agrave; rất cần thiết sau những chuyến trekking, leo n&uacute;i. Nhưng h&atilde;y ch&uacute; &yacute; bổ sung nước sớm v&agrave;o buổi tối, hạn chế uống qu&aacute; nhiều nước trước khi đi ngủ. Tuy nhi&ecirc;n nếu lỡ c&oacute; uống m&agrave; muốn giải quyết v&agrave;o giữa đ&ecirc;m th&igrave; c&aacute;ch duy nhất l&agrave; giải quyết xong rồi bạn sẽ c&oacute; một giấc ngủ ngon, cố nhịn chỉ l&agrave;m bạn mất ngủ hơn m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Đảm bảo: đủ tho&aacute;ng kh&iacute; trong lều: Cảm gi&aacute;c ngột ngạt, kh&oacute; chịu sẽ khiến t&acirc;m trạng bạn xấu đi, ảnh hưởng đến việc đi ngủ. H&atilde;y đảm bảo lều lu&ocirc;n th&ocirc;ng tho&aacute;ng, V&iacute; dụ như mở cửa sổ, mở cửa th&ocirc;ng gi&oacute; v&agrave; chọn đ&uacute;ng hướng dựng lều. Đồng thời kh&ocirc;ng gian trong lều cũng phải đủ thoải m&aacute;i cho bạn v&agrave; những đồng đội kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; - Giữ ấm: Điều kiện ngủ l&yacute; tưởng nhất l&agrave; khi bạn giữ ấm đủ để cơ thể thoải m&aacute;i đi v&agrave;o giấc ngủ. Một số mẹo để đảm bảo bạn đủ ấm khi đi ngủ l&agrave;: H&atilde;y lấy t&uacute;i ngủ ra để n&oacute; phồng l&ecirc;n nhất định sau một thời gian d&agrave;i bị n&eacute;n, việc h&uacute;t kh&ocirc;ng kh&iacute; v&agrave;o b&ecirc;n trong lớp b&ocirc;ng sẽ tăng khả năng giữ ấm của t&uacute;i ngủ. Nếu thời tiết qu&aacute; lạnh bạn c&oacute; thể chứa một chai nước ẩm (Đảm bảo chai nước c&oacute; thể chứa nước s&ocirc;i nha - v&iacute; dụ như b&igrave;nh Nalgene), bỏ v&agrave;o trong t&uacute;i</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; -ngủ để ủ ấm trước khi bạn bắt đầu giấc ngủ. V&agrave; tất nhi&ecirc;n việc lựa chọn đ&uacute;ng độ d&agrave;y t&uacute;i ngủ l&agrave; quan trọng nhất, h&atilde;y lựa chọn t&uacute;i ngủ thật kỹ. Đồng thời mũ len, tất v&agrave; găng tay l&agrave; những phụ kiện hỗ trợ đắc lực nếu thời tiết qu&aacute; lạnh, h&atilde;y mang theo ch&uacute;ng nh&eacute;</span></p>\r\n', 1),
(18, 'asdf', 'Hướng dẫn chung về leo núi', '<p><strong><span style=\"font-size:24px\">Mặc d&ugrave; một số người leo n&uacute;i trong nh&agrave; kh&ocirc;ng bao giờ mạo hiểm vượt ra ngo&agrave;i khu vực kiểm so&aacute;t của họ, nhưng đối với hầu hết ch&uacute;ng ta, leo n&uacute;i ngo&agrave;i trời chắc chắn l&agrave; một vị tr&iacute; đ&aacute;ng để thử th&aacute;ch trong tương lai. Nếu bạn quyết định thử th&aacute;ch ch&iacute;nh m&igrave;nh, th&igrave;&nbsp;hướng dẫn leo n&uacute;i ngo&agrave;i trời&nbsp;n&agrave;y sẽ hỗ trợ bạn.</span></strong></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><img alt=\"\" src=\"https://travelgear.vn/blog/wp-content/uploads/2019/06/huong-dan-leo-nui-ngoai-troi-4.jpg?fbclid=IwAR0ADp6nQHjXXI6LWAr6Oq8GAMMx06B_1fXqVclCPMJZ9EoTRMXWXn5RycM\" style=\"height:534px; width:800px\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">Thế giới ngo&agrave;i trời đ&ograve;i hỏi những kỹ năng v&agrave; kiến thức tương tự như leo n&uacute;i trong nh&agrave;, nhưng n&oacute; cũng đưa ra những th&aacute;ch thức bổ sung. Bạn cần phải chuẩn bị nhiều hơn, c&oacute; thiết bị ph&ugrave; hợp v&agrave; th&agrave;nh thạo c&aacute;c kỹ năng mới.</span></p>\r\n\r\n<p style=\"margin-left:40px\"><br />\r\n<span style=\"font-size:24px\"><strong>1. T&igrave;m một hướng dẫn</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Bước đầu ti&ecirc;n của bạn khi chuyển sang leo n&uacute;i mạo hiểm l&agrave; t&igrave;m một hướng dẫn vi&ecirc;n được chứng nhận để đưa bạn đến một khu vực l&acirc;n cận gần đ&oacute;, hoặc t&igrave;m một người hướng dẫn c&oacute; tr&igrave;nh độ với kinh nghiệm giảng dạy cho những người leo n&uacute;i mạo hiểm. Một người hướng dẫn tốt c&oacute; thể gi&uacute;p bạn hiểu l&agrave;m thế n&agrave;o để leo l&ecirc;n trong mọi t&igrave;nh huống, v&agrave; sẽ c&oacute; thể dạy cho bạn c&aacute;c kỹ năng n&acirc;ng cao hơn như thiết lập v&agrave; l&agrave;m sạch c&aacute;c neo của ri&ecirc;ng bạn.</span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:24px\"><strong>2. Thiết bị để leo n&uacute;i ngo&agrave;i trời</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><img alt=\"\" src=\"https://travelgear.vn/blog/wp-content/uploads/2019/06/thiet-bi-leo-nui-ngoai-troi.jpg?fbclid=IwAR09cXWWQbqUuO2xG-V5rS5eccuPBFvBrmlt43PtA13AojWh4YFaooKrikU\" style=\"height:340px; width:800px\" /></span></p>\r\n\r\n<p><br />\r\n<strong><span style=\"font-size:18px\">&nbsp; &nbsp;&nbsp;</span><span style=\"font-size:24px\">Mượn thiết bị</span></strong></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Mặc d&ugrave; ban đầu bạn sẽ muốn mượn thiết bị để tiết kiệm tiền, nhưng h&atilde;y cẩn thận. Kh&ocirc;ng bao giờ mượn bất kỳ phần cứng hoặc thiết bị an to&agrave;n quan trọng n&agrave;o kh&aacute;c nếu bạn kh&ocirc;ng biết chi tiết về lịch sử đầy đủ của n&oacute;. Phần cứng c&oacute; thể c&oacute; thiệt hại tiềm ẩn v&agrave; c&aacute;c th&agrave;nh phần nylon (như d&acirc;y đai bảo hộ, c&aacute;p treo v&agrave; d&acirc;y thừng) c&oacute; thể xuống cấp do m&agrave;i m&ograve;n, tia UV hoặc thậm ch&iacute; l&agrave; do chất lỏng. Do đ&oacute; bạn kh&ocirc;ng c&oacute; nhiều lựa chọn vay mượn ngoại trừ quần &aacute;o, gi&agrave;y v&agrave; phấn.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp;&nbsp;</span><span style=\"font-size:24px\"><strong>Mua thiết bị</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Người hướng dẫn của bạn n&ecirc;n cung cấp một danh s&aacute;ch tất cả c&aacute;c thiết bị bạn cần mua trước khi đi chơi hoặc đến lớp.</span></p>\r\n\r\n<p><strong><span style=\"font-size:18px\">&nbsp; &nbsp;&nbsp;</span></strong><span style=\"font-size:24px\"><strong>Nhu cầu về quần &aacute;o</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Trang phục tập gym đẹp, co gi&atilde;n của bạn vẫn ổn khi mặt trời chiếu nhẹ v&agrave; nhiệt độ &ocirc;n h&ograve;a. Nhưng bạn cũng muốn c&aacute;c lớp th&iacute;ch hợp trong trường hợp thời tiết trở n&ecirc;n m&aacute;t mẻ hoặc ẩm ướt. Ở mức tối thiểu, thật tuyệt khi c&oacute; một chiếc &aacute;o kho&aacute;c ấm để mặc khi bạn chờ đến lượt khi leo n&uacute;i.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:18px\">&nbsp; &nbsp;&nbsp;</span></strong><span style=\"font-size:24px\"><strong>3. Lựa chọn leo n&uacute;i ngo&agrave;i trời cho người mới bắt đầu</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><img alt=\"\" src=\"https://travelgear.vn/blog/wp-content/uploads/2019/06/huong-dan-leo-nui-ngoai-troi-1.jpg?fbclid=IwAR0rHDlHwYygnfV5fVsD0YTvrmSiZ2MQBVuJqwQPNbi2K_ZkCn_zmbY8VKk\" style=\"height:333px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Phong c&aacute;ch kh&aacute;c nhau của leo n&uacute;i ngo&agrave;i trời đ&ograve;i hỏi mức độ kinh nghiệm kh&aacute;c nhau. C&aacute;c t&ugrave;y chọn tốt nhất cho những người mạo hiểm b&ecirc;n ngo&agrave;i lần đầu ti&ecirc;n được liệt k&ecirc; dưới đ&acirc;y.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><strong>&nbsp; &nbsp; </strong></span><span style=\"font-size:24px\"><strong>Leo khối đ&aacute;</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Leo khối đ&aacute; l&agrave; leo n&uacute;i ngo&agrave;i trời được thực hiện tr&ecirc;n mặt đ&aacute;, tảng đ&aacute; v&agrave; c&aacute;c bề mặt kh&aacute;c gần mặt đất. Kh&ocirc;ng cần d&acirc;y thừng hoặc d&acirc;y đai bảo hộ, v&igrave; vậy bạn kh&ocirc;ng cần nhiều thiết bị leo n&uacute;i để thử n&oacute;. N&oacute; cực kỳ phổ biến với những người leo n&uacute;i bắt đầu bởi v&igrave; gần mặt đất hơn l&agrave;m giảm bớt yếu tố sợ h&atilde;i. V&agrave; leo khối đ&aacute; l&agrave; một hoạt động tuyệt vời để t&igrave;m hiểu c&aacute;c kỹ thuật mới.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Nếu bạn thử sức m&igrave;nh, bạn c&oacute; thể t&igrave;m một lớp giới thiệu hoặc lớp leo n&uacute;i. Hoặc bạn c&oacute; thể đi với những người leo n&uacute;i c&oacute; kinh nghiệm, những người c&oacute; thể đ&oacute;ng vai tr&ograve; l&agrave; người chạy hỗ trợ trong khi bạn leo l&ecirc;n. Mặc d&ugrave; bạn tương đối gần mặt đất, nguy cơ chấn thương vẫn lu&ocirc;n hiệu hữu ở đ&oacute;.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; </span><span style=\"font-size:24px\">Leo n&uacute;i với neo tr&ecirc;n đỉnh</span><br />\r\n<br />\r\n<span style=\"font-size:18px\"><img alt=\"\" src=\"https://travelgear.vn/blog/wp-content/uploads/2019/06/huong-dan-leo-nui-ngoai-troi.jpg?fbclid=IwAR0OkuhmPBSHdPaCpFxFQ8nMZ1LNIyNKkyo2vCA55-2cj_0zpHo2oFsOkAc\" style=\"height:400px; width:800px\" /><br />\r\n&nbsp; &nbsp; - Leo n&uacute;i với neo tr&ecirc;n đỉnh sẽ tương tự như khi leo ở ph&ograve;ng tập thể dục của bạn. Bạn leo về ph&iacute;a một mỏ neo ở đầu tuyến đường trong khi hướng dẫn vi&ecirc;n hoặc người hướng dẫn của bạn sẽ đỡ b&ecirc;n dưới. Sự kh&aacute;c biệt ch&iacute;nh ở ngo&agrave;i trời l&agrave; bạn phải sử dụng tay v&agrave; ch&acirc;n tự nhi&ecirc;n kh&ocirc;ng c&oacute; mỏm nh&acirc;n tạo nh&ocirc; ra khi bạn leo l&ecirc;n. Bởi v&igrave; điều n&agrave;y, người hướng dẫn của bạn c&oacute; thể sẽ đưa bạn v&agrave;o c&aacute;c tuyến đường c&oacute; mức độ thấp hơn so với bạn thường l&agrave;m khi leo n&uacute;i trong nh&agrave;.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; </span><strong><span style=\"font-size:24px\">C&aacute;c h&igrave;nh thức leo n&uacute;i n&acirc;ng cao</span></strong></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - C&aacute;c tuyến leo n&uacute;i thể thao ngo&agrave;i trời thường c&oacute; chốt khoan v&agrave;o đ&aacute; v&agrave; bạn phải thực hiện chốt d&acirc;y hai đầu nhanh để kẹp v&agrave;o.<br />\r\nBan đầu, khi bạn chơi thể thao leo n&uacute;i, bạn l&agrave;m một thứ gọi l&agrave; leo n&uacute;i nhanh. Điều đ&oacute; đ&ograve;i hỏi cả sợi d&acirc;y bạn kẹp v&agrave;o v&agrave; một sợi d&acirc;y tr&ecirc;n c&ugrave;ng. Kỹ thuật n&agrave;y cho ph&eacute;p bạn thực h&agrave;nh b&aacute;m v&agrave;o chốt nhanh m&agrave; kh&ocirc;ng cần phải rơi xa khi bạn mắc lỗi.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; </span><span style=\"font-size:24px\"><strong>T&igrave;m một địa điểm để leo l&ecirc;n</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Ngo&agrave;i s&aacute;ch hướng dẫn, Internet l&agrave; một nguồn tốt để t&igrave;m hiểu về c&aacute;c khu vực v&agrave; tuyến đường leo n&uacute;i.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; </span><span style=\"font-size:24px\"><strong>4. Quy tắc khi leo n&uacute;i ở ngo&agrave;i trời</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><img alt=\"\" src=\"https://travelgear.vn/blog/wp-content/uploads/2019/06/huong-dan-leo-nui-ngoai-troi-2.jpg?fbclid=IwAR3XjUfixC8FA_NmIwxp_vjXHmgK4xtx5kJMjUME99LWWW-bSimkTKfOSB4\" style=\"height:336px; width:800px\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Giống như bất kỳ hoạt động ngo&agrave;i trời, leo n&uacute;i c&oacute; một quy tắc ứng xử. Tuy nhi&ecirc;n, c&aacute;c nguy&ecirc;n tắc kh&ocirc;ng để lại dấu vết rất quan trọng v&igrave; số lượng lớn người leo n&uacute;i đang dần di chuyển v&agrave;o một số khu vực leo n&uacute;i ngo&agrave;i trời hạn chế. Cộng đồng Access Fund, chủ trương cho những người leo n&uacute;i tr&ecirc;n to&agrave;n thế giới, b&aacute;o c&aacute;o rằng một trong năm khu vực bị đ&oacute;ng cửa v&igrave; c&aacute;c vấn đề về tr&aacute;ch nhiệm ph&aacute;p l&yacute; hoặc những người leo n&uacute;i kh&ocirc;ng c&ograve;n được ch&agrave;o đ&oacute;n.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\">&nbsp; &nbsp; - Đọc v&agrave; l&agrave;m theo The Pact, một cam kết đặc biệt được ph&aacute;t triển bởi Access Fund, l&agrave; một nơi tuyệt vời để bắt đầu:<br />\r\nT&ocirc;n trọng người leo n&uacute;i kh&aacute;c.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">Xử l&yacute; chất thải của con người đ&uacute;ng c&aacute;ch.</span></li>\r\n	<li><span style=\"font-size:18px\">Chỉ đậu xe v&agrave; cắm trại trong khu vực được chỉ định.</span></li>\r\n	<li><span style=\"font-size:18px\">Ở tr&ecirc;n những con đường m&ograve;n đ&atilde; được thiết lập.</span></li>\r\n	<li><span style=\"font-size:18px\">Đặt thiết bị v&agrave; miếng đệm tr&ecirc;n bề mặt bền, phẳng</span></li>\r\n	<li><span style=\"font-size:18px\">L&agrave;m sạch phấn v&agrave; những nơi đ&aacute;nh dấu.</span></li>\r\n	<li><span style=\"font-size:18px\">Giữ cấu h&igrave;nh thấp, giảm thiểu k&iacute;ch thước nh&oacute;m v&agrave; tiếng ồn.</span></li>\r\n	<li><span style=\"font-size:18px\">Đ&oacute;ng g&oacute;i tất cả r&aacute;c, miếng đệm v&agrave; thiết bị.</span></li>\r\n	<li><span style=\"font-size:18px\">T&ocirc;n trọng giờ đ&oacute;ng cửa</span></li>\r\n	<li><span style=\"font-size:18px\">H&atilde;y l&agrave; người l&ecirc;n tiếng, kh&ocirc;ng phải l&agrave; người đứng nh&igrave;n ngo&agrave;i cuộc</span></li>\r\n</ul>\r\n', 1);

